style.css:
@-moz-document url-prefix("http://your.qbittorrent.url/") {
html, body, #desktopHeader, #desktopNavbar, #mochaToolbar {
    background-color: #222 !important;
    color: #ddd;
}

#desktopNavbar li a {
    color: #ddd !important;
    border-bottom-color: #777 !important;
}

#desktopNavbar li a:hover {
    background-color: #666 !important;
}

#desktopNavbar li ul {
    background-color: #333 !important;
    background-image: none !important;
}

#desktopNavbar li ul .divider {
    border-top-color: #555 !important;
}

#pageWrapper {
    border-top-color: #777 !important;
}

.torrentTable, .dynamicTable {
    border-color: #555 !important;
}

.torrentTable th, .dynamicTable th {
    background-color: #333 !important;
    color: #ddd !important;
}

.dynamicTable th {
    border-right-color: #777 !important;
}

.torrentTable tr, .dynamicTable tr {
    background-color: #222 !important;
}

.torrentTable tr:nth-child(even), .dynamicTable tr:nth-child(even) {
    background-color: #333 !important;
}

.torrentTable tr.selected, .torrentTable tr:hover, .dynamicTable tr.selected, .dynamicTable tr:hover {
    color: #fff !important;
}

.torrentTable tr.selected, .dynamicTable tr.selected {
    background-color: #354158 !important;
}

.torrentTable tr:hover, .dynamicTable tr:hover {
    background-color: #e60 !important;
}

.toolbarTabs {
    padding-bottom: 0 !important;
}

.toolbarTabs li, .toolbarTabs li a {
    background-image: none !important;
    color: #ddd !important;
}

.toolbarTabs li {
    margin-left: 2px !important;
    margin-right: 2px !important;
    margin-bottom: 0 !important;
    background-color: #555 !important;
    border-radius: 10px 10px 0 0;
    box-shadow: 0 0 7px #111;
}

.toolbarTabs li.selected {
    background-color: #777 !important;
}

.toolbarTabs li:hover {
    background-color: #999 !important;
}

.toolbarTabs li a {
    margin-left: 0 !important;
    padding: 4px 9px !important;
}

#propertiesPanel_header {
    height: 27px !important;
}

#propertiesPanel_headerContent {
    padding-top: 3px !important;
}

#prop_general fieldset, #watched_folders_tab tr, #watched_folders_tab th, #watched_folders_tab td {
    border-color: #999 !important;
}

#desktopFooter {
    background-color: #333 !important;
    color: #ddd !important;
}

.panel {
    background-color: #222 !important;
    border-bottom-color: #777 !important;
    color: #ddd !important;
}

.panel-header, .panel-headerContent, .toolbarTabs {
    background-image: none !important;
    background-color: #222 !important;
}

.panel-header {
    border-bottom-color: #777 !important;
}

.horizontalHandle, .columnHandle {
    background-color: #444 !important;
}

.horizontalHandle {
    background-image: none !important;
}

.mochaCanvas {
    display: none !important;
}

.mochaTitlebar, .mochaTitlebar.replaced {
    background-color: #444 !important;
    border-radius: 7px 7px 0 0;
}

.mochaTitle {
    color: #ddd !important;
}

.mochaToolbarWrapper {
    border-top-color: #aaa !important;
}

.mochaToolbar {
    border-top-color: #777 !important;
    background-color: #222 !important;
    padding-top: 4px;
}

.mochaContentBorder {
    border-top-color: #777 !important;
    border-bottom-color: #444 !important;
    border-bottom-width: 23px !important;
    border-radius: 0 0 7px 7px;
}

.mochaContentWrapper {
    background-color: #222 !important;
}

.mochaContent fieldset {
    border-color: #777 !important;
}

.mocha::after {
    content: "";
    display: block;
    position: absolute;
    top: 4px;
    left: 5px;
    bottom: 7px;
    right: 5px;
    border-radius: 7px;
    box-shadow: 0 0 10px #000;
    z-index: -1000;
}

.mocha#preferencesPage::after {
    bottom: 6px;
}

.contextMenu, .contextMenu li ul {
    background-color: #333 !important;
}

.contextMenu li a {
    color: #ddd !important;
}

.contextMenu li a:hover {
    background-color: #666 !important;
}

h2, h3 {
    color: #ddd;
}
}
