body, a {text-shadow: 0 0 10px rgba(255, 255, 255, .25);}
.cadre-message a:hover {text-shadow: 0 0 10px rgba(255, 255, 255, 1); color: #6495ed;}
.cadre-message {border: 1px solid #243b4d; text-shadow: none;}
.cadre-repondre {background: #1e1915; border: 0; margin-bottom: 5%;}
legend {border: 0;}
.cadre-forum {box-shadow: 1px 1px -1px #1e1915}
footer#footer {border-top: 1px solid #000; padding: .5% 0 .1%;}
div.avatar-profil {padding-top: 5px;}
td span.pull-right, td div.cadre-sujet-actions {padding: 0 5px 0 5px;}
td div.bloc-checkbox-selection {padding-left: 10px;}
div.span12 div.groupe-boutons-barre-droite {padding-right: .65%;}
div.cadre-spoil div {padding-top: 5px;}
img, a.cadre-section-titre-mini img.img24, div.element-composant-auteur div.bouton-nom img.bouton-profil-avatar:hover {
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -o-border-radius: 5px;
    border-radius: 5px;
	-webkit-filter: drop-shadow(0 0 10px rgba(0, 0, 0, .7));
    filter: drop-shadow(10px 10px 7px rgba(0, 0, 0, .7));
    -ms-filter: drop-shadow(10px 10px 7px rgba(0, 0, 0, .7));
    -o-filter: drop-shadow(10px 10px 7px rgba(0, 0, 0, .7));
    -moz-filter: drop-shadow(10px 10px 7px rgba(0, 0, 0, .7));
}
img.espace-2-2 {-webkit-border-radius: 0; -moz-border-radius: 0; border-radius: 0; -o-border-radius: 0;}
div.cadre-sujet {padding: 5px;}
table span.cadre-type-auteur-joueur, table span.cadre-type-auteur-moderateur, table span.cadre-type-auteur-admin,
table span.cadre-type-auteur-sentinelle, table span.cadre-type-auteur-mapcrew, div.cadre-message-message span.cadre-message-modere-texte,
td.table-cadre-cellule-numero a.numero-message, div.cadre-spoil span.titre-spoiler {font-weight: 500;}
a.accordion-toggle img {padding-right: 10px;}
button.bouton-quote {font-size: 12px;}
.dropdown-menu li a.element-menu-principal img, a.bouton-barre-navigation img {padding-right: 5px;}
a.bouton-barre-navigation img {padding-left: -20px;}
div.bouton-nom-profil span.cadre-utilisateur-nom img {padding: 0 -10px 10px 15px;}
div.barre-navigation a.active img.img16 {padding-bottom: 2px; padding-right: 5px;}
form#create_dialog fieldset legend {padding: 5px 0 0 10px;}
div.span12 div.cadre-formulaire {background: #1e1915; padding-bottom: 50px;}
a:active, a.accordion-toggle span.cadre-type-auteur-joueur:visited {text-decoration: none;}
div.cadre-relief.cadre-utilisateur {padding-bottom: 5px;}
div.cadre-message-modere {border: 1px solid #cb546b;}
div#corps div.span12 a.cliquable img:hover {-webkit-animation: giro 5s infinite; animation: giro 5s infinite; -o-animation: giro 5s infinite; -moz-animation: giro 5s infinite;}
@-webkit-keyframes giro{
    from {transform: rotate(0deg); -o-transform: rotate(0deg); -webkit-transform: rotate(0deg); -moz-transform: rotate(0deg);}
    to {transform: rotate(360deg); -o-transform: rotate(360deg); -webkit-transform: rotate(360deg); -moz-transform: rotate(360deg);}}
@keyframes giro {
    from {transform: rotate(0deg); -o-transform: rotate(0deg); -webkit-transform: rotate(0deg); -moz-transform: rotate(0deg);}
    to {transform: rotate(360deg); -o-transform: rotate(360deg); -webkit-transform: rotate(360deg); -moz-transform: rotate(360deg);}}
div.element-composant-auteur div.bouton-nom img.bouton-profil-avatar {
    transition: all .2s ease-in-out, padding .2s linear, border-radius .2s, opacity .8s;
    -moz-transition: all .2s ease-in-out, padding .2s linear, border-radius .2s, opacity .8s;
    -webkit-transition: all .2s ease-in-out, padding .2s linear, opacity .8s, border-radius .2s;
    -o-transition: all .2s ease-in-out, padding .2s linear, opacity .8s, border-radius .2s;
}
div.element-composant-auteur div.bouton-nom img.bouton-profil-avatar:hover {
    transform: scale(1.1);
    -moz-transition: all .2s ease-in-out;
    -webkit-transition: all .2s ease-in-out;
    -o-transition: all .2s ease-in-out;
    padding-left: -2px;
    border-radius: 5px;
}
div.element-composant-auteur div.bouton-nom img.bouton-profil-avatar:active {opacity: .4;}
td.table-cadre-cellule-principale a.cadre-sujet-titre.lien-blanc img {
    transition: all 1s ease-in-out;
    -moz-transition: all 1s ease-in-out;
    -webkit-transition: all 1s ease-in-out;
    -o-transition: all 1s ease-in-out;
}
body {font-family: 'Roboto', sans-serif; font-weight: 400;}
div.menu-principal .lien-titre, button.bouton-quote {font:"Helvetica Neue",Helvetica,Arial,sans-serif;}
}
