style.css:
@namespace svg url(http://www.w3.org/2000/svg);

@-moz-document url-prefix("https://www.nicovideo.jp/watch/") {
* {
  border-radius: 0 !important;
  box-shadow: none !important;
  text-shadow: none !important;
}
body {
}

#siteHeader #siteHeaderInner,
.WatchAppContainer-switchToFlash .SwitchToFlashLead-inner,
.FooterContainer-content {
  width: 984px !important;
}

.FooterContainer {
  min-width: 984px !important;
}
/*==================================================================
                      .WatchAppContainer-main
==================================================================*/
.WatchAppContainer-main {
  position: relative !important;
  margin: 0 auto !important;
  padding: 0 4px !important;
  box-sizing: content-box !important;
  width: 976px !important;

  color: #393f3f !important;
  line-height: 1.375 !important;
  font-size: 12px !important;
}

.WatchAppContainer-main a {
  color: #292f2f !important;
  text-decoration: underline !important;
}
.WatchAppContainer-main a.watch:link {
  color: #596979 !important;
  font-weight: bold;
}
.WatchAppContainer-main a.watch:visited {
  color: #292F2F !important;
}
.WatchAppContainer-main p {
  margin: 0 !important;
  line-height: 1.375 !important;
}

/*==================================================================
                      .HeaderContainer
==================================================================*/
.HeaderContainer {
  position: relative !important;
  padding: 88px 0 5px !important;
}
  .Grid.HeaderContainer-row:first-child {
    margin-bottom: 0 !important;
    z-index: 2 !important;
  }
  .HeaderContainer-videoTitle {
    padding-right: 0 !important;
  }
  .HeaderContainer-videoTitle > .VideoTitle {
    font-size: 16px !important;
  }

  /*===========検索バー===========*/
  .HeaderContainer-searchBox {
    width: unset !important;
    height: 48px !important;
    position: absolute !important;
         top: -76px !important;
        left: 56px !important;
    padding-left: 200px !important;
    background: url("data:image/png;base64,") no-repeat !important;
  }
    .SearchBox {
        margin: 16px 0 0 8px !important;
      width: auto !important;
      height: 26px !important;
      box-sizing: border-box !important;
    }
    /*===========タブ===========*/
/*  もはやこれまで
    .SearchBox > .SearchBox-optionButton {
      display: none !important;
    }
*/
    .SearchBox > .SearchBox-optionMenu {
       bottom: 25px !important;
       left: -1px !important;
      display: block !important;
      border: 0 !important;
      background: none !important;
    }
    .SearchBox > .SearchBox-optionMenu > .SearchBox-optionMenuItem {
      display: inline-block !important;
      margin-right: 2px !important;
      padding: 2px 3px 0 !important;
      height: 14px !important;
      line-height: 14px !important;
      background: #d9d9d9 !important;
      color: #393f3f !important;
      box-sizing: initial !important;
    }
    .SearchBox[action^="/search/"] > .SearchBox-optionMenu > .SearchBox-optionMenuItem:first-child,
    .SearchBox[action^="/tag/"] > .SearchBox-optionMenu > .SearchBox-optionMenuItem:last-child {
      background: #333 !important;
      color: #fff !important;
    }
    .SearchBox > .SearchBox-optionMenu > .SearchBox-optionMenuItem > .SearchBox-optionMenuIcon {
      display: none !important; 
    }

    .SearchBox > .SearchBox-input {
      width: 236px !important;
      padding: 0 4px 0 !important;
    }
    .SearchBox > .SearchBox-submitButton {
      position: relative !important;
      width: 32px !important;
      padding: 6px !important;
      border-left: 1px solid #d9d9d9 !important;
    }
    .SearchBox > .SearchBox-submitButton > .SearchBox-submitIcon {
      fill: #393f3f !important;
    }
  /*===========動画説明===========*/
  .VideoDescriptionContainer {
    margin: 25px 0 6px !important;
    z-index: 2 !important;
  }
  .VideoDescription,
.VideoDescription-plain {
     padding: 0 !important;
      font-size: 12px !important;
  }
    .VideoDescription.is-not-expandable:before,
    .VideoDescription.is-expanded:before {
      content: "動画説明" !important;
      margin: 0 2px 0 8px !important;
      padding: 5px 7px 6px !important;
      position: relative !important;
           top: -2px !important;
      box-shadow: 0 0 0 1px #fff inset !important;
      border: solid #393f3f !important;
      border-width: 2px 2px 0 !important;
      border-radius: 4px 4px 0 0!important;
      color: #333 !important;
      background: linear-gradient(to bottom, #cbd0d0, #fff) !important;
    }
    .VideoDescription .VideoDescription-html {
      width: 712px !important;
      border: 2px solid #393f3f !important;
      padding: 8px !important;
      line-height: 1.375 !important;
      font-size: 12px !important;
    }

  .VideoDescriptionExpander-switch {
    padding: 0 !important;
    top: -60px !important;
    bottom: unset !important;
    right: -250px !important;
  }

  /*===========投稿者情報・投稿・再生===========*/
  .VideoMetaContainer > div {
    border: 0 !important;
  }
  .VideoDescriptionContainer + .HeaderContainer-row {
    position: absolute !important;
         top: 120px !important;
      bottom: 0 !important;
       right: 0 !important;
    display: block !important;
    margin: 0 !important;
    z-index: 1 !important;
  }
    /*投稿者情報*/
    .VideoDescriptionContainer + .HeaderContainer-row > .VideoOwnerInfo-gridCell,
    .VideoDescriptionContainer + .HeaderContainer-row > .col-fill > .ChannelInfo {
      position: absolute !important;
         right: 0 !important;
      z-index: 2 !important;
  width: 256px !important;
  padding: 6px !important;
  border: 2px solid #999f9f !important;
  background: #e9efef !important;
    }
    .VideoDescriptionContainer + .HeaderContainer-row > .col-fill > .ChannelInfo {
         top: 65px !important;
      height: unset !important;
    }
    /*投稿・再生*/
    .VideoDescriptionContainer + .HeaderContainer-row > .col-fill,
    .VideoDescriptionContainer + .HeaderContainer-row > .col-full {
      position: absolute !important;
           top: -65px !important;
        bottom: 0 !important;
          left: 0 !important;
      width: 100% !important;
    }

    .VideoMetaContainer > .VideoUploadDateMeta > .VideoUploadDateMeta-title {
      display: inline !important;
    }
    .VideoMetaContainer > .VideoUploadDateMeta > .VideoUploadDateMeta-dateTimeLabel {
      font-size: inherit !important;
      margin: 0 0 0 .5em !important;
    }
    .VideoMetaContainer > .CategoryRankMeta > .CategoryRankMeta-title {
      display: inline !important;
    }
    .VideoMetaContainer > .CategoryRankMeta > .CategoryRankMeta-title:before {
      content: "…";
      margin: 0 .5em !important;
    }

    .VideoMetaContainer > .CategoryRankMeta > .CategoryRankMeta-yesterdayRank {
      margin: 0 .5em 0 !important;
    }

    /*再生数たち*/
    .VideoViewCountMeta,
    .CommentCountMeta,
    .MylistCountMeta {
    position: absolute;
    right: 163px !important;
    width: 130px !important;
    padding: 0 5px !important;
    background: #000 !important;
  }

  .VideoViewCountMeta {
    bottom: -22px !important;
  }

  .CommentCountMeta {
    bottom: -39px !important;
  }

  .MylistCountMeta {
    bottom: -56px !important;
  }
    .VideoViewCountMeta:before {
      content: "再生:";
      color: #fff;
    }
    .CommentCountMeta:before {
      content: "コメント:";
      color: #fff;
    }
    .MylistCountMeta:before {
      content: "マイリスト:";
      color: #fff;
    }
    .VideoViewCountMeta-title,
    .CommentCountMeta-title,
    .MylistCountMeta-title {
      display: none !important;
    }
    .VideoViewCountMeta-counter,
    .CommentCountMeta-counter,
    .MylistCountMeta-counter {
      margin: 0 !important;
      color: #fff !important;
      font-size: 12px !important;
    }
    .VideoViewCountMeta-counter > span,
    .CommentCountMeta-counter > span,
    .MylistCountMeta-counter > span {
      float: right !important;
    }


  /*===========タグ===========*/
  .TagContainer {
    max-width: 712px !important;
    margin: 0 !important;
    padding: 0 !important;
   /* padding: 0 0 0 56px !important;
    background: url("data:image/png;base64,") no-repeat left center !important;
   */ z-index: 2 !important;
  }
    .TagContainer-TagEditButtonArea {
      order: 2;
      display: inline !important;
      float: left !important;
    }
    div.TagContainer-area:nth-child(2) {
    }
    .TagItem {
      background: none !important;
      border: 0 !important;
      padding: 1px 20px 0 0 !important;
      margin: 0 4px 0 0 !important;
      height: unset !important;
      line-height: unset !important;
    }
    .TagItem.is-editing.is-locked:not(.is-category) {
      padding: 0 0 0 20px !important;
    }

    .TagItem > .TagItem-name {
      font-weight: unset !important;
      font-size: 12px !important;
      line-height: 1.375em !important;
    }
    .TagItem > .TagItem-nicoDicLink {
        top: 0 !important;
     bottom: 0 !important;
    }

/*==================================================================
                      .MainContainer
==================================================================*/

.MainContainer {
  box-shadow: 0 0 0 2px #000 !important;
  border: 1px #ccc solid !important;
  background: #666 !important;
  padding: 2px !important;
  margin: 2px 2px 12px !important;
}

  /*===========プレイヤー===========*/
  .MainContainer-player {
    width: unset !important;
  }
    /* ニコニコニュース*/
    .MarqueeContainer {
      width:674px !important;
    }
    .MarqueeContainer a {
      color: #fff !important;
    }

    /*プレイヤー*/
    .PlayerContainer {
      width: unset !important;
    }
      .VideoContainer {
        width:674px !important;
        height: 378px !important;
      }

  /*===========コメント欄===========*/
  .MainContainer-commentPanel {
    width: 288px !important;
    margin: 58px 2px 2px !important;
    z-index: 2 !important;
  }
    /*上*/
    .VideoMenuContainer {
      position: absolute !important;
           top: -102px !important;
          left: 37px !important;
      width: 256px !important;
    }
    .VideoMenuContainer:after {
      display: none !important;
    }

      .VideoMenuContainer-areaLeft,
      .VideoMenuContainer-areaRight {
         margin: 0 !important;
         padding: 0 !important;
         background: none !important;
      }
      .VideoMenuContainer-areaLeft > .ClickInterceptor {
        margin: 0 !important;
      }
      .VideoMenuContainer-areaLeft > .ClickInterceptor > .InstantMylistButton {
        width: 96px !important;
      }
      .VideoMenuContainer-areaLeft > .ClickInterceptor:nth-of-type(2) > .VideoMenuContainer-button {
        width: 80px !important;
      }
      .VideoMenuContainer-areaLeft > .ClickInterceptor:nth-of-type(3) > .UadButton {
        width: 80px !important;
      }
      .VideoMenuContainer-areaLeft > .ClickInterceptor > .InstantMylistButton > svg {
        background: url("data:image/png;base64,") no-repeat !important;
      }
      .VideoMenuContainer-areaLeft > .ClickInterceptor:nth-of-type(2) > .VideoMenuContainer-button > svg {
        background: url("data:image/png;base64,") no-repeat !important;
      }
      .VideoMenuContainer-areaLeft > .ClickInterceptor:nth-of-type(3) > .UadButton > svg {
        background: url("data:image/png;base64,") no-repeat !important;
      }
      .VideoMenuContainer-areaLeft > .ClickInterceptor svg > path,
      .VideoMenuContainer-areaLeft > .ClickInterceptor svg > rect {
        display: none !important;
      }
      .VideoMenuContainer-areaLeft > .ClickInterceptor > .VideoMenuContainer-uadContainer > .UadButtonBalloon {
        display: none !important;
      }
      .VideoMenuContainer-areaLeft .ActionButton {
        height: 32px !important;
        margin: 0 !important;
        padding: 0 !important;
      }
      .VideoMenuContainer-area:first-child .ActionButton path {
        display: none !important;
      }
      .VideoMenuContainer-area:last-child {
        background: unset !important;
        width: unset !important;
      }

    /*下*/
    .CommentPanelContainer {
           top: 0 !important;
         bottom: 0 !important;
        width: 288px !important;
        border: 2px solid !important;
    }
      .CommentPanelContainer-content,
      .CommentPanelMenuContainer-mainMenu,
      .CommentPanelMenuContainer-mainMenuArea {
        position: absolute !important;
             top: 0 !important;
          bottom: 0 !important;
            left: 0 !important;
           right: 0 !important;
      }
      .CommentPanelMenuContainer {
        position: absolute !important;
             top: 6px !important;
          bottom: 6px !important;
            left: 0px !important;
           right: 0px !important;
        border: 0 !important;
      }
      /*過去ログ*/
      .CommentPanelMenuContainer.is-pastCommentMode > .CommentPanelMenuContainer-pastCommentMenu {
             top: 26px !important;
      }
      .CommentPanelContainer-content {
            left: 6px !important;
           right: 6px !important;
      }
      .CommentPanelMenuContainer-mainMenu {
        height: 100% !important;
      }
        .CommentPanelMenuContainer-mainMenuArea > .ActionButton {
          padding: 0 !important;
        }
        .CommentPanelMenuContainer-mainMenuArea > .ReloadButton {
          position: absolute !important;
            bottom: 0 !important;
             right: 0 !important;
        }
        .CommentPanelMenuContainer-mainMenuArea > .CalendarButton {

          position: static !important;
          opacity: 1 !important;
            top: 0 !important;
              left: 0 !important;
        }
        .CommentPanelMenuContainer-mainMenuArea > .ReloadButton > .ReloadButton-icon {
          display: none !important;
        }
        .CommentPanelMenuContainer-mainMenuArea > .ReloadButton:after {
          content: "再読み込み" !important;
          position: static !important;
          opacity: 1 !important;
          border: 0 !important;
          background: none !important;
          color: unset !important;
        }
        .CommentPanelMenuContainer-mainMenuArea > .CommentPanelAutoScrollButton {
          position: absolute !important;
            bottom: 0 !important;
              left: 0 !important;
        }

        .CommentPanelMenuContainer-mainMenuArea > .CommentPanelAutoScrollButton:after {
          content: "自動でスクロール" !important;
          position: static !important;
          opacity: 1 !important;
          border: 0 !important;
          background: none !important;
          color: unset !important;
        }
      /*過去ログ*/
      .CommentPanelMenuContainer-pastCommentMenu {
        height: auto !important;
        padding: 0 !important;
      }
        .DateTimeInput-date {
          width: 120px !important;
        }
        .DateTimeInput-time {
          width: 60px !important;
        }

      .CommentPanelContainer-commentPanel {
           top: 30px !important;
        bottom: 30px !important;
      }

      /*過去ログ表示*/
      .is-pastCommentMode + .CommentPanelContainer-commentPanel {
           top: 65px !important;
      }
        .CommentPanelDataGrid-dataGrid {
          border: 1px solid !important;
        }
        [class^="___header-table___"] {
          border-bottom: 1px solid !important;
        }
        .react-grid-Container > .react-grid-Main > .react-grid-Grid {
          position: static !important;
          min-height: unset !important;
        }
          /*コメントヘッダー*/
          .CommentPanelDataGrid-headerCell {
            height: 20px !important;
            line-height: 20px !important;
            padding-left: 5px !important;
            border-right: 1px solid #666 !important;
          }
          .react-grid-HeaderCell {
            line-height: 20px !important;
            font-weight: 100 !important;
             padding-left: 5px !important;
          }

          /*コメント中身*/
          [class^="___table-cell___"] {
            border-right: 1px solid #666 !important;
          }
            .CommentPanelDataGrid-cell {
              padding-left: 3px !important;
              text-overflow: unset !important;
            }

/*==================================================================
                      .BottomContainer
==================================================================*/
.BottomContainer {
  width: 976px !important;
}
  /*左側*/
  .BottomContainer-main {
    padding-right: 10px !important;
  }
  /*右側*/
  .BottomContainer-side {
    width: 266px !important;
  }
/*==================================================================
                      kokokokoukoku
==================================================================*/
.NicoSpotAdContainer,
.MiniBannerAdContainer,
.BottomContainer-adContainer {
  display: none !important;
}
}
