@-moz-document url-prefix("https://www.youtube.com") {
body {
    background: url(http://www.ccvalg.pt/astronomia/galaxias/galaxias_irregulares/pequena_nuvem_magalhaes.jpg) no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}
/* MASTER HEAD */

#yt-masthead-container {
    background-color: #239000;
    border-bottom: 0px solid;
}
.appbar-hidden #masthead-positioner-height-offset {
    height: 52px;
    background-color: #239000;
}
/* MASTER HEAD - Logo */

#yt-masthead #logo-container .logo {
    background: url(data:image/png;base64,);
}
/* MASTER HEAD - New Upload Button */

.yt-uix-button-icon-material-upload {
    background: url(data:image/png;base64,);
}
.yt-uix-button-opacity,
.yt-uix-button-opacity:hover {
    opacity: 100
}
/* MASTER HEAD - Notification Active Bell */

#yt-masthead-user .yt-uix-button-icon-bell,
#yt-masthead-user .yt-uix-button-icon-bell:hover {
    background: url(data:image/png;base64,);
    opacity: 100
}
/* MASTER HEAD - Notification Active Bell */

#yt-masthead-user .sb-notif-on .yt-uix-button-icon-bell {
    background: no-repeat url(data:image/png;base64,);
    background-size: auto;
    opacity: 100;
}
#yt-masthead-user .sb-notif-on .yt-uix-button-icon-bell:hover {
    opacity: 50;
}
/* MASTER HEAD - Sidebar Button */

.show-guide .yt-uix-button-icon-appbar-guide,
.show-guide #masthead-positioner:hover .yt-uix-button-icon-appbar-guide {
    background: url(data:image/png;base64,) !important;
}
.yt-uix-button-icon-appbar-guide {
    background: url(data:image/png;base64,) !important;
}
/* MASTER HEAD - Sidebar Button Box */

button#appbar-guide-button {
    box-shadow: 0 0 0 0;
    background-color: transparent;
    border: 0px solid;
}
button#appbar-guide-button:hover {
    box-shadow: 0 0 0 0;
    background-color: transparent;
    border: 0px solid;
}
button#appbar-guide-button:active {
    box-shadow: 0 0 0 0;
    background-color: transparent;
    border: 0px solid;
}
/* MASTER HEAD - Sidebar Button Arrow */

.not-yt-legacy-css #masthead-positioner #appbar-guide-button:hover .yt-uix-button-arrow,
.yt-legacy-css #masthead-positioner:hover #appbar-guide-button .yt-uix-button-arrow {
    border-top-color: #FFF;
}
.not-yt-legacy-css #masthead-positioner #appbar-guide-button .yt-uix-button-arrow,
.yt-legacy-css #masthead-positioner:hover #appbar-guide-button .yt-uix-button-arrow {
    border-top-color: #FFF;
}
/* MASTER HEAD - Region Abreviation Text Color */

#yt-masthead #logo-container span.content-region {
    color: #FFFFFF;
}
/* Full Page Alert (Added 12-8-15) */

.yt-alert-default.yt-alert-info,
.yt-alert-actionable.yt-alert-info,
.yt-alert-naked.yt-alert-info .yt-alert-icon,
.yt-alert-small.yt-alert-info {
    background: #239000;
    border-bottom: 1px solid #e8e8e8;
}
/* MASTER SIDEBAR - Active Page */

.guide-item.guide-item-selected,
.guide-item.guide-item-selected:hover {
    background: #239000;
}
/* MASTER SIDEBAR - Section Headings */

li.guide-section h3 a {
    color: #239000;
}
/* Page Navagation Tabs */

a.yt-uix-button-epic-nav-item.partially-selected:hover,
button.yt-uix-button-epic-nav-item:hover,
button.yt-uix-button-epic-nav-item.selected,
button.yt-uix-button-epic-nav-item.yt-uix-button-toggled,
.epic-nav-item:hover,
.epic-nav-item.selected,
.epic-nav-item.yt-uix-button-toggled,
.epic-nav-item-heading {
    border-color: #239000;
}
.feed-header-feed-filter .yt-uix-button-epic-nav-item.selected,
.feed-header-feed-filter .yt-uix-button-epic-nav-item:hover {
    border-color: #239000;
}
a.yt-uix-button-epic-nav-item:hover,
a.yt-uix-button-epic-nav-item.selected,
a.yt-uix-button-epic-nav-item.yt-uix-button-toggled {
    border-color: #239000;
}
.ytp-swatch-background-color,
.ytp-swatch-background-color-secondary {
    background-color: #239000;
}
/* TRANSPERENT PLAYER - Anotations & Auto Play Slider Buttons */

.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
    background: #239000;
}
/* TRANSPERENT PLAYER - Volume Slider */

.ytp-volume-slider-handle:before {
    background: #239000;
}
/* TRANSPERENT PLAYER - Other Stuff */

.ytp-settings-button.ytp-hd-quality-badge:after,
.ytp-settings-button.ytp-4k-quality-badge:after,
.ytp-settings-button.ytp-5k-quality-badge:after,
