}
.ytp-menuitem sup {
    color: #239000;
}
/* WATCH PAGE - AutoPlay Slider Button */

.yt-uix-checkbox-on-off input[type=checkbox]:checked+label {
    background-color: #239000;
}
/* WATCH PAGE - Like Scale */

.video-extras-sparkbar-likes {
    background: #239000;
}
/* CREATOR PAGES - Tabs */

.creator-editor-nav-tabs li.selected,
.creator-editor-nav-tabs li:hover,
.epic-nav-item.selected,
.epic-nav-item:hover,
.menu-tab:hover,
.menu-tab.selected {
