style.css:
@-moz-document domain("facebook.com") {
*[dir=rtl],
	html[dir=rtl] > body,
	div[dir=rtl],
	span[dir=rtl],
	.webMessengerMessageGroup ._38 p,
	._6nm,
  	.direction_rtl,
	._1mk,._58jw,._5pbx, .userContent,.mtm, ._5pco,
	html[dir=rtl] *	{

		font-family: Jameel,"Jameel Noori Nastaleeq", Alvi Lahori Nastaleeq, Alvi Nastaleeq, Tahoma !important;
		font-size: 150% !important;
		line-height: 155% !important;
	}
}

@-moz-document regexp("http://(urdu)\\.geo\\.tv/.*") {
body,
h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: Jameel, "Jameel Noori Nastaleeq", Alvi Lahori Nastaleeq, Alvi Nastaleeq, Tahoma !important;
}
}

@-moz-document domain("twitter.com") {
*[dir=rtl],
    div[dir=rtl],
    span[dir=rtl],
    .tweet-text-rtl {
		font-family: Jameel,"Jameel Noori Nastaleeq", Alvi Lahori Nastaleeq, Alvi Nastaleeq, Tahoma !important;
		font-size: 125%;
		line-height: 145%;
    }
}

@-moz-document domain("ur.wikipedia.org") {
*[dir=rtl],
    html[dir=rtl] > body,
    div[dir=rtl],
    span[dir=rtl],
    html[dir=rtl] * {
		font-family: "Jameel Noori Nastaleeq", Alvi Lahori Nastaleeq, Alvi Nastaleeq, Tahoma !important;
		font-size: 105%;
    }
}

