style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nos.nl") {
  
  html, body {
    background-color: #111;
    color:#aaa;
  }
  
  .nav {
    background-color: #000;
  }
  
  .subnav {
    background-color: #6c6c6c;
    color: #aaa;
  }
  
  .subnav a{
    color: #ccc;
  }
  
  .subnav a:hover {
    color: #fff;
  }
  
  .section-sub {
    background-color: #111; 
  }
  .article_head-cover .article_head_meta{
    color:#d8d8d8;
  }
  
  #footer {
    background-color: #000;
  }
  /* some other stuff */
  
  .link-red, a:hover .link-hover {
    color: #fff;
  }
  
  a {
    color: #c8c8c8;
  }
  
  a:hover {
    color: #fff;
  }
  
  body.homepage #topstories .topstory_mainarticle, body.homepage #topstories {
    color: #ccc;
  }
}
