style.css:
@-moz-document regexp("https?://(www|player|go).twitch.tv/.*") {
/*
 * Name:    Dark Twitch -by NORWIN-
 * Author:  NORWIN  "https://userstyles.org/users/110629"
*/
.gdpr-consent-banner, .pl-pinned-panel, .anon-front__social-container, .player-alert, #dfp-directory-banner, #dfp-directory-rectangle, .consent-banner{display:none!important;}

body{background: #333!important;font-family: Helvetica Neue,Helvetica,Arial,sans-serif!important;}
html *:not(.chat-author__display-name):not(a):not([data-a-target="edit-display-name"]), h5, .channel-header__verified, .tw-c-text-alt, .tw-c-text-base, ::placeholder {color:#bbb!important}
a{color:#fff!important}
a:hover {color:#999!important;text-decoration:none!important;}

/***logo***/
.tw-animated-glitch-logo__body, .tw-animated-glitch-logo__eyes {fill: #555!important;}
.tw-animated-glitch-logo__face {fill: #bbb!important;}
/***ballon***/
.balloon{background: #212121!important;border: 1px solid #111!important;box-shadow:none!important}
/***input.text***/
input.text, input.countries-input, input.string, textarea, .tw-input, .tw-textarea:focus{background: #212121!important;border: 1px solid #1c1c1c!important;box-shadow:none!important}
:focus:not(input) {outline-color: transparent!important;box-shadow:none!important;border-color:transparent!important;background:transparent!important;}
/***button***/
.tw-core-button, .tw-button, .carousel-player-nav-arrow__container{background: #212121!important; box-shadow:none!important;border: none!important;}
#root .tw-core-button:hover:not(#emote-menu-button), #root .tw-button:hover{background: #333!important; box-shadow:none!important;border: none!important;}
.tw-root--hover .tw-interactable--inverted:hover{background: #333!important; box-shadow:none!important;border: none!important;}
.modal-wrapper__content .tw-core-button--secondary:hover:not(.tw-core-button--disabled), .modal-wrapper__content .tw-button:hover:not(.tw-button--disabled){background: #333!important; box-shadow:none!important;border: none!important;}
[data-a-target="chat-settings"], [data-a-target="chat-viewer-list"], [data-a-target="prime-offers-icon"], .onsite-notifications .tw-core-button, [data-a-target="whisper-box-button"], [data-test-selector="close-viewer-card"], [data-a-target="ellipsis-button"], .extension-panel .tw-core-button{background: transparent!important; box-shadow:none!important;border: none!important;}
    /*suchbutton*/
    [data-a-target="tray-search-input"] .tw-core-button{background: #212121!important;border: 1px solid #1c1c1c!important;box-shadow:none!important}
    [data-a-target="tray-search-input"] .tw-core-button--disabled {opacity: 1!important;}
    /*button im player*/
    [data-a-target="player-controls"] button {background: transparent!important;}
    #root [data-a-target="player-controls"] button:hover {background: hsla(0,0%,100%,.1)!important;}
/***border***/
.tw-border-b, .tw-border-t{border-color:transparent!important}
[data-a-target="tw-box-art-card-link"] .tw-hover-accent-effect, [data-a-target="tw-box-art-card-link"] .tw-hover-accent-effect__corner {background: #444!important;}

/***select auswahl***/
.tw-select{background-color: transparent!important; }
.tw-select *{background-color: #212121!important;box-shadow:none!important;border:none!important; }
.tw-checkbox__input:checked+.tw-checkbox__label:before, .tw-root--theme-dark .tw-checkbox__input:checked+.tw-checkbox__label:before, .tw-root--theme-light .tw-checkbox__input:checked+.tw-checkbox__label:before, .tw-checkbox__label:before {background: #444!important;box-shadow: inset 0 0 0 1px #444!important;border: 1px solid #444!important;}

/***startseite online***/
.front-page-carousel .carousel-metadata, .front-page-carousel .carousel-metadata--fadeout {background: #212121!important;}

/***startseite offline***/
.anon-front__featured-section{background: #1a1a1a!important; }
.horizontal-carousel-player__details--overlay{background: #111!important; box-shadow:none!important;border: none!important;}
#root .carousel-nav__item {border-bottom: 4px solid #333 !important;}
#root .carousel-nav__item--active {border-bottom: 4px solid #999 !important;}

/***footer***/
.footer{background:#212121 url(http://file1.npage.de/010270/19/bilder/commentsback1.jpg)!important;border-top: 1px solid #111!important;}

/***durchsuchen***/
.directory-header-new__banner-cover {background: linear-gradient(0deg,#333 0,#3333339c 25%,#3333339e)!important;}
    /*slider*/
    .preview-card-carousel__button .tw-interactable--disabled, .preview-card-carousel__button .tw-interactable:disabled{background:#000!important;opacity: .5;}
    .preview-card-carousel__button .tw-interactable--base{background:#666!important}
    .preview-card-carousel__button .tw-interactable--base:hover{background:#212121!important}

/***top nav***/
#root .top-nav__menu, #root .minimal-top-nav__menu{background:#212121 url(http://file1.npage.de/010270/19/bilder/commentsback1.jpg)!important;border-bottom: none!important;box-shadow:none!important;}
.prime-offer-list--vertical .prime-offer-list__content:after, .prime-offer, .search-result-section__titlesep, .tw-pill--brand, .bits-buy-card-offer-row{background: transparent!important;border:none!important;}
[data-a-target="user-menu-toggle"]:hover{background: #212121!important;box-shadow:none!important;border:none!important;}
.tw-interactable--alpha:hover:not([data-a-target="whisper-box-button"]){background: #333!important;box-shadow:none!important;border:none!important;}
:focus {outline:none!important}
/***left nav***/
.side-nav__theme-wrapper *:not(.tw-channel-status-indicator):not(.recommended-friends), .side-nav__overlay-wrapper {background-color:transparent!important; box-shadow:none!important;color:#bbb!important;border-color:#555!important}
.side-nav .side-nav__theme-wrapper, #root [data-a-target="side-nav-bar"].tw-c-background-alt, #root [data-a-target="side-nav-bar-collapsed"].tw-c-background-alt{background:#212121 url(http://file1.npage.de/010270/19/bilder/commentsback1sv.jpg)!important;/*border-right: 1px solid #111!important;*/box-shadow:none!important;}
.side-nav-card .side-nav-card__link:hover, .recommended-friends{background: #212121!important;box-shadow:none!important;border:none!important;}
.rich-content-tooltip__pointer-target a:hover{background: #333!important;box-shadow:none!important;border:none!important;}
/***chat***/
.pinned-cheer-v2, .pinned-cheer-v2__loading, .channel-leaderboard{display:none!important;}
#root .right-column [class^="channel-"]:not(.channel-points-icon):not(.channel-points-icon__image), .video-watch-page__right-column, .stream-chat{background:#212121 url(http://file1.npage.de/010270/19/bilder/commentsback1sv.jpg)!important;border-left: 0px solid #111!important;}
.chat-viewers__pane{background:#212121 url(http://file1.npage.de/010270/19/bilder/commentsback1.jpg)!important;}
.channel-root__right-column .tw-border-l, .qa-vod-chat{border-left:none!important;}
.room-picker, .room-upsell, .room-upsell:after, .chat-share-prompt, .emote-grid-section__header-title{background: #212121!important;border:none!important;}
.reward-gift-user-notice, .user-notice-line {border-left: 4px solid #00000061!important;}
.chat-line__message .mention-fragment.mention-fragment--recipient {background: #9f0303 !important;}
.chat-line__message .mention-fragment, .chat-room, .rooms-header, .stream-chat-header {background: transparent !important;}
.tw-tooltip, .tw-tooltip:after {background:#0e0c13!important}
.chat-line__message-body--highlighted {background: #444!important;border: 4px solid #444!important;}
    /*chatpopup*/
    .popout-chat-page, [data-a-page-loaded-name="PopoutChatPage"] .room-selector{background:#212121 url(http://file1.npage.de/010270/19/bilder/commentsback1.jpg)!important;}
    .tw-root--hover .tw-c-background-alt, .chat-room__content.tw-c-text-base{background: transparent!important;border:none!important;}
    .pinned-cheer-v2 {display:none}
    /*chatsquad*/
    .multi-stream-player-layout__chat{background:#212121 url(http://file1.npage.de/010270/19/bilder/commentsback1.jpg)!important;}
    /*chatbits*/
    .t-bits-card__footer{background: #212121!important; }
    /*userKarte*/
    [data-a-target="viewer-card"]{border:none!important;}
    .bttv-moderator-card-messages .label .triangle{border-left: 7px solid #999!important;}
    .bttv-moderator-card-messages {background: #212121!important;border:none!important;}
    /*emotekarte*/
    .emote-card{background: #212121!important;border:none!important;}
    /*videochat*/
    .vod-message.vod-message--focused, .vod-message:hover {background-color: transparent!important;}
    .video-chat__input {box-shadow: none!important;}
    /*submeldung*/
    [data-test-selector="chat-private-callout-queue__callout-container"] .tw-c-background-accent{background: #212121!important;border:none!important;}
    /*bttv#bttv-channel-state-contain*/
    #bttv-channel-state-contain{float: right!important;}
    /*einstellungbutton*/
    .chat-input__buttons-container .tw-align-content-center.tw-align-items-center.tw-flex.tw-flex-row {width: 100%!important;}
    [data-a-target="chat-send-button"]{right: 4%;position: absolute!important;align-items: center!important;}
    [data-test-selector="chat-input-buttons-container"] .tw-align-content-center {align-items: normal!important;}
    [data-a-target="chat-settings-balloon"]{position:fixed!important;margin-right: 11px!important;bottom: 35px!important;}
    [data-test-selector="chat-input-buttons-container"] .tw-z-default {z-index: auto!important;}
    /*emotebuttonbttv*/
    #emote-menu-button, #emote-menu-button:focus {background-color:transparent!important;background-size:20px!important;background-position: center!important;background-repeat: no-repeat!important;border:none!important;box-shadow:none!important;}
/***stream***/
/**new05.20**/
.channel-root, .channel-root--watch .channel-root__info, .channel-root__info .channel-info-content, .home-header-sticky, .home-header-sticky__inner, .home-header-sticky__wrapper {background-color: #333!important;}
.channel-root--home .channel-info-content {width: calc(100%)!important;}
.tw-channel-status-text-indicator {background-color: #333!important;}
.channel-root--live .user-avatar-animated__halo, .tw-root--theme-dark .channel-root--live .user-avatar-animated__halo, .tw-root--theme-light .channel-root--live .user-avatar-animated__halo {background:transparent!important;}
.live-indicator-container{background: #bbb!important;}
.live-indicator-container .tw-c-background-inherit {background-color: transparent!important;}
/*zeitplan*/
.stream-schedule__header, .stream-schedule-day-name{background-color:#212121!important}
.stream-schedule-day-name__is-today{background-color:#444!important}
[data-a-target="stream-schedule-day"] .tw-c-background-accent{background-color:#444!important}
/**ENDnew05.20**/

main {background: #333!important;}
main *{border:none!important;}

.channel-header, .channel-header__item:before {background: #333!important;border:none!important}
.channel-header__user-avatar:after, .channel-header__item--selected:after, .channel-header__item--selected:hover:after, .channel-header__item:active:after{border-bottom-color: #bbb!important;}

.channel-info-bar{background: #333!important;box-shadow:none!important;border: none!important;}
#root .tw-c-background-accent-alt-2, #root .tw-c-background-base:not(.whispers-threads-box__container):not(.top-nav__menu):not(.rooms-header):not(.stream-chat-header), .tw-c-background-base:not(.whispers-threads-box__container):not(.top-nav__menu):not(.rooms-header):not(.stream-chat-header), .modal__backdrop .tw-c-background-base, .tw-balloon, [data-a-target="threads-box-open"]{background-color: #212121!important;box-shadow:none!important;border: none!important;}
#root .tw-c-background-alt-2, #root .tw-c-background-alt, [data-a-target="threads-box-closed"]{background: transparent!important;box-shadow:none!important;border: none!important;}

.tw-interactable--alt, .tw-tag{background:transparent!important; box-shadow:none!important;border:none!important}
[data-a-target="channel-viewers-count"] path {fill: #ec1313!important;}
[data-a-target="channel-viewers-count"] .tw-stat__value{color:#ec1313!important;}
    /*squad & host*/
    .channel-root__player-container .tw-c-background-accent{background-color:#212121!important}
    /*erweiterungs panel*/
    .extension-panel *{box-shadow:none!important;border:none!important}
    /*fl√ºster*/
    .thread-header__title-bar-container--focused {background-color: transparent!important;}
    .thread-header__title-bar-container {background: transparent!important;box-shadow: none!important;border:none!important}
    .whispers-list-item{box-shadow:none!important;border: none!important;}
    .whispers-list-item:hover{background: #333!important;box-shadow:none!important;border: none!important;}
    /*follow&glocke_botton&subbotton*/
    .follow-btn--animated, .live-notifications__btn, .follow-btn--following-icon-btn{background: transparent!important;box-shadow:none!important;border: none!important;}
    .channel-header__follow-btn--following .tw-core-button, [data-test-selector="channel-header__social-buttons-animated-flow"] .tw-button--success, .follow-btn__follow-notify-container [data-a-target="unfollow-button"], .follow-btn__dropdown-toggle, .follow-btn--with-dropdown, .channel-header__right .tw-button--success{background: #065cff3d!important;box-shadow:none!important;border: none!important;}
    .follow-btn__dropdown-toggle:hover{background: #065cff12!important;box-shadow:none!important;border: none!important;}
    .follow-btn__follow-notify-container [data-a-target="unfollow-button"], [data-a-target="live-notifications-wrapper--on"] .tw-core-button{background: #065cff3d!important;box-shadow: none!important;border: none!important;}
    [data-a-target="live-notifications-wrapper--off"] .tw-core-button, html #root [data-a-target="live-notifications-wrapper--off"] .tw-c-background-base, [data-a-target="subscribed-button"]{background: #333!important; background-color:#333 !important;box-shadow:none!important;border: none!important;}
    [data-a-target="subscribed-button"]:hover{background: #212121!important;box-shadow:none!important;border: none!important;}
    #root .follow-btn__follow-notify-container [data-a-target="unfollow-button"]:hover{background: #bb1411!important;box-shadow:none!important;border: none!important;}
    .follow-btn__follow-btn-container.follow-btn__follow-btn-container--following {transform: translateX(44px);}
/***streamer off info***/
.ReactModal__Overlay .tw-c-background-alt-2{background: #333!important;}
/***subs***/
.modal__backdrop {background-color: rgba(0, 0, 0, 0.69)!important;}
.checkout-modal__container.tw-c-background-alt, .checkout-form__modal-header, .checkout-form__container .summary-layout{background-color: #212121!important;box-shadow:none!important;border: none!important;}
/***anmelden***/
.modal__backdrop input{background: #444!important;border: 1px solid #111!important;box-shadow:none!important}
/***subsListe***/
.subscription-card *{border:none!important}
.subs-checkout-root{background: #444!important;}
/***zahlungen***/
.payment-methods-tab *:not(.button__text), .tw-table-heading{background-color: #212121!important;box-shadow:none!important;border: none!important;}
.tw-table .tw-table-row:nth-child(2n) .tw-table-cell {background:#212121!important}
.tw-table-row:nth-child(odd) {background:#1c1c1c!important}
/***dashboard***/
    /*leftnav*/
    .dashboard-side-nav{background:#212121 url(http://file1.npage.de/010270/19/bilder/commentsback1sv.jpg)!important;border-right: 0px solid #111!important;box-shadow:none!important;}
    .dashboard-side-nav__link--active, .dashboard-side-nav__link:hover{background: #212121!important;box-shadow:none!important;border:none!important;}
    /*main*/
    .live-page-banner-wrapper-inner .tw-elevation-3{background: #444!important;}
    .twilight-root, .quest-header{background: #444!important;}
    #root .extension-card__thumbnail__details-preview, .ach-card, .tw-interactable--base{background: #212121!important;border:none!important; box-shadow:none!important}
    .drag-and-drop-card-container{background:#212121 url(http://file1.npage.de/010270/19/bilder/commentsback1.jpg)!important;border-radius:14px!important}
    .dashboard-centered-page .tw-drop-zone{background: #212121!important;}
    .clmgr-table__row {background:#212121!important}
    .clmgr-table__row:nth-child(odd) {background:#1c1c1c!important}
/***benachrichtigung***/
.persistent-notification__unread {background-color: #444!important;border:none!important;}
.persistent-notification__read{background:transparent!important;border:none!important;}
/**primebotton**/
.prime-offers__pill .tw-c-background-accent{background-color:transparent!important}
}
