/* root */
#ajax-loading, #ajax-error { position: fixed; }
#ajax-loading { background: rgba(0, 0, 0, 0.5) url("http://image.torrent.kg/images/IKZ0831.gif") no-repeat scroll center center;
    border-bottom-left-radius: 10px; height: 30px; width: 85px; right: 0; }
#ajax-error { background-color: #d32031; border-bottom-right-radius: 10px; min-height: 30px; }
* {font-family: 'Noto Sans', sans-serif; } body, th, td, font { font: 12px "Noto Sans",Verdana,Arial,sans-serif; }
body { background: #c0b8aa none repeat scroll 0 0; color: #000000; min-width: 960px; }
#body_container, html { background: /*[[background]]*/ repeat scroll 0 0; padding: 0 0 4px; }
