/* header navigation 1st */
#main_nav { background: #c0b8aa url("http://image.torrent.kg/images/TIu8390.jpg") repeat-x scroll 0 0;
    height: 24px; margin: 0; padding: 8px 10px 2px; text-transform: uppercase; border: none; }
#main_nav a { color: #fbecda; font-size: 11px; text-shadow: 1px 1px 1px #000; border-right: 1px solid #484540; }  
#main_nav a:hover, #main_nav a:active { color: #ffc961 !important; }
#main_nav_2nd_group li a[href="http://image.torrent.kg"] { border: 0 none; }
/* logo background */
div#logo { background: rgba(0, 0, 0, 0) url("http://image.torrent.kg/images/92r1297.jpg") repeat-x scroll center center; }
div#logo table tbody tr td a { display: block; -moz-box-sizing: border-box; box-sizing: border-box;
    background: /*[[logo]]*/ }
/* header nav 2st */  
#logon { background: rgba(0, 0, 0, 0) url("http://image.torrent.kg/images/8Uk6419.png") repeat-x scroll center top; height: 34px; border: none; padding: 0 15px 0 8px;} 
div#logon span#hello_username:nth-child(1) span, span#hello_username span label[for="autologin_chbox"] { color: #000; text-shadow: 1px 1px 1px #fbecda; }
div#logon a, .new_pm #pm_info a:link, .new_pm #pm_info a:visited { color: #E5E4E2; text-shadow: 1px 1px 1px #000; }  
div#logon a:hover { color: #C0C0C0 !important; }
.new_pm { border: 0 !important; }
#logon > span:nth-child(6) > a:nth-child(1) > img:nth-child(1) { margin-top: 8px; } #logon > span:nth-child(1) { padding-top: 6px; }
#logon > span:nth-child(3) { padding-top: 8.5px; }
span#charity_span, span#logged_out_links { margin-top: 8.5px; }  
#logon > span:nth-child(2) > span#pm_info { margin-top: 7px; background: url(http://image.torrent.kg/images/v9j0660.png) no-repeat ; }
#logon > span:nth-child(2) > span#pm_info > a { position: absolute; top: 8px !important; right: 1%; text-transform: uppercase; color: #fbecda; text-shadow: 1px 1px 1px #000; }
#logon > span:nth-child(2) > span#pm_info > a:hover { color: #ffc961 !important; }
/* new_pm animation >> */
.new_pm #pm_info a[href^="privmsg.php?folder=inbox"]:link, .new_pm #pm_info a[href^="privmsg.php?folder=inbox"]:visited
    { -webkit-animation: newmsg 2s linear infinite; animation: newmsg 2s linear infinite; }
@-webkit-keyframes newmsg { 0% { color: #ffc961; } 50% { margin-right: 10px; } 100% { color: #ffc961; } }
@keyframes newmsg { 0% { color: #ffc961; } 50% { margin-right: 10px; } 100% { color: #ffc961; } }
/* << new_pm animation */
span#hello_username span { margin-top: 2.5px; } #logon > span:nth-child(3) > span:nth-child(1) { margin-top: 0.6px; }
