select, select[class="prow1"]
{ padding-right: 15px; appearance: none; background-image: url("http://www.torrent.kg/forum/images/strelka.png"), linear-gradient(#e5e4e2, #c0c0c0 38%, #e5e4e2);
  background-position: right center; background-repeat: no-repeat; border-radius: 5px; }
option { border-bottom: 1px solid #C0C0C0; padding-bottom: 2px; }
