style.css:
@-moz-document domain("google.com") { }

@-moz-document url-prefix("http://www.google"),              url-prefix("https://www.google"){

.srp #searchform {
    position: fixed;
  }



}



@-moz-document url-prefix("http://www.bing"),url-prefix("https://www.bing"),url-prefix("http://cn.bing"),url-prefix("https://cn.bing"){


body #b_header {
	height: 120px; 
   padding-bottom: 0px;
	position: fixed;
	background-color: #FFF;
}
#b_content {
    line-height: 18px;
    margin-top: 150px;
    float: left;
}
#b_context .b_ans, #b_context #wpc_ag {
    margin: 0px -20px 10px -21px;
}
#b_results {
    width: 590px;
}


}
