style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("animeaddicts.hu") {
/*[[gif]]*/
/*[[youtube]]*/
/*[[flash]]*/

div.spoiler
{background-color: #CECECE !important;}

div[id^="signature_"]
{opacity: 0.5 !important;}

img,td,table,ul,input,div,li,a,select,button
{border-radius: 0px !important;
box-shadow: 0px 0px 0px !important;}

button:hover,img.thumb2.i16:hover
{animation: pulzalas 2s ease-out !important; animation-iteration-count: infinite !important;}

img.thumb,img.thumb2
{box-shadow: 0px 0px 0px; padding: 0 !important; margin: 2px !important;}


a>img.thumb:hover, ul.tabs li:hover
{filter: brightness(1.5);}

.tooltip
{z-index: 10002 !important;}
.tooltip2
{z-index: 10002 !important;}

div#dhtmltooltip
{background-color: grey !important; opacity: 0.8 !important;}

div.headline2,div.headline,td.headline,ul.tabs li
{background: linear-gradient(#3E5984, #1C2840) !important;}
ul.tabs li
{height: 38px !important; border-bottom: 2px solid #2196E9 !important;}
li.active a
{color: white !important;}
li.active
{filter: brightness(1.2) !important; height: 36px !important; border-top: 4px solid #2196E9 !important;}
ul.tabs li:hover
{height: 36px !important; border-bottom: 4px solid #2196E9 !important; transition: height 0.1s !important;}

#bottom-menu
{width: 990px !important; height: 85px !important; background: url("http://kephost.com/images/2017/01/20/strike_witches_246_1_blur.jpg") fixed top !important; border: 1px solid #999 !important;}
#bottom-menu>ul
{width: 990px !important;}
#bottom-menu li:hover
{animation: pulzalas 2s ease-out !important; animation-iteration-count: infinite !important;}
#bottom-menu li
{margin: 2px !important; left: 2px !important;}

/*[[logo]]*/
#headerpic
{background: url("http://kephost.com/images/2017/01/20/strike_witches_246_1_blur.jpg") no-repeat fixed top !important;}

body
{background: url("http://kephost.com/images/2017/01/20/strike_witches_246_1.jpg") no-repeat fixed top !important;}

div#content/*majd ha lesz backdrop-filter*/
{backdrop-filter: blur(2px) !important;}


#top
{opacity: 0.9; background: linear-gradient(#737373, #3d3d3d) !important; z-index: 10001; border-bottom: 1px solid #999 !important; position: fixed; box-shadow: 0 1px 2px black !important; height: 75px !important; width: 100% !important;}

#login
{margin: 0px 0 0 0 !important; opacity: 0.9 !important; padding-left: 463px !important; backdrop-filter: blur(2) !important;}

#online
{z-index: 10001; position: fixed; right: 47% !important; text-shadow: 1px 1px 1px #000000 !important}

#search>p
{opacity: 0.9; z-index: 10001; border: 0px; border-radius: 0px; position: fixed; right: 27%; top: 45px; text-shadow: 1px 1px 1px #000000 !important;}

#search>form
{top: 10px !important; z-index: 10001; position: fixed; right: 464px !important; opacity: 0.7;}

#search-text
{width: 320px !important;}

#search-send:hover,#login>.settings:hover,div.thumb.twinkle.touchable:hover,[id*="ot3-"]>div.naptar>a:hover,.headline2>ul>li:hover
{animation: pulzalas 2s ease-out !important; animation-iteration-count: infinite !important;}

div.titlebox>ul>li,div.titlebox>div.button
{background: none !important; box-shadow: 1px 0 0 0 #cecece !important;}

div.titlebox>ul>li:hover,div.titlebox>div.button:hover
{background-color: #cecece !important; transition: background-color 0.3s !important;}

img.thumb2:hover
{animation: pulzalas2 2s ease-out !important; animation-iteration-count: infinite !important;}


#menu-container
{background: none !important; padding-top: 76px !important;}
#menu-ul
{background: linear-gradient(#B9D6FE,#0391F5) !important;}


div.box
{background: url("http://kephost.com/images/2017/01/20/strike_witches_246_1_blur.jpg") no-repeat fixed top !important;}
div.headline3,td.headline3
{background: url("http://kephost.com/images/2017/01/20/strike_witches_246_1_blur2.jpg") no-repeat fixed top !important; box-shadow: 0px 2px 2px #cecece !important;}
div.titlebox,img#slide-prev,img#slide-next,table.noborder,[class="headline4"]
{background: url("http://kephost.com/images/2017/01/20/strike_witches_246_1_blur3.jpg") no-repeat fixed top !important;}

div.news-footer
{width: 718px !important; background: url("http://kephost.com/images/2017/01/20/strike_witches_246_1_blur2.jpg") no-repeat fixed top !important; margin: 0px 0px 5px; border-left: 1px solid #999999 !important; border-right: 1px solid #999999 !important; border-bottom: 1px solid #999999 !important;}

div.news-footer div
{background-color: rgba(130,130,130,0.3) !important; margin: 4px -2.2px 4px 4px !important; width: 176.2px !important; box-shadow: 0 0px 1px white !important;}

div.news-footer div:hover,div.headline3:hover
{background-color: #888888 !important; transition: background-color 0.3s !important;}

div.touchable
{background: 0; border-radius: 0 !important;}

div.box.news
{margin: 0px 0px 0px 0px !important; border-bottom: 0px !important; width: 708px !important;}

div.box.news>div.headline3
{width: 701px !important;}

div.box.news>div.content
{width: 701px !important;}

div.content
{font-size: 15px !important;}

div.titlebox>a>img:hover
{filter:brightness(1.2) contrast(1.1);}

img[src*="./theme/modules/forum/bg.jpg"]
{width: 0px; height: 55px; padding-left: 6px; background-image: none !important;}

img[src*="./theme/images/bottom-bg.jpg"]
{width: 0px; height: 38px; padding-left: 1px; background: none !important;}


div#right>div.box>table.noborder>tbody>tr>td[class="right"]>div
{font-size: 16px !important;}

[class="mylist"]:hover
{background-color: #1081D0 !important; transition: background-color 1s !important;}

[id="button_selected"]
{color: #28B4FF !important; filter: drop-shadow(0px 0px 1px grey) !important;}

/*--------------------------------------*/
/*pulzálás animáció*/
@-webkit-keyframes pulzalas {
0% { }
50% { border-color: #1081D0; filter: brightness(1.8);}
100% { }
}

@keyframes pulzalas {
0% { }
50% { border-color: #1081D0; filter: brightness(1.8);}
100% { }
}

.pulse {
-webkit-animation-name: pulzalas;
animation-name: pulzalas;}
/*pulzálás vége*/

/*pulzálás animáció2*/
@-webkit-keyframes pulzalas2 {
0% { filter: brightness(1.1);}
50% { border-color: #1081D0; filter: brightness(1.0);}
100% { filter: brightness(1.1);}
}

@keyframes pulzalas2 {
0% { filter: brightness(1.1);}
50% { border-color: #1081D0; filter: brightness(1.0);}
100% { filter: brightness(1.1);}
}

.pulse {
-webkit-animation-name: pulzalas2;
animation-name: pulzalas2;}
/*pulzálás2 vége*/
