style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("comments.dumpert.nl") {
  
  a {
    color: green  !important;
  }
  
  h1 {
    color: white !important;
  }
  
  body {
    color: #CCCCCC !important;
  }
  
  textarea {
    background-color: #2A2A2A !important;
    color: #CCCCCC !important;
    border: none !important;
  }

  .comment {
    color: #CCCCCC !important;
    border-bottom: 1px solid #2A2A2A !important;
  }
  
  .comment footer {
    color: #808080 !important;
  }
  .comment.topcomment {
    color: #CCCCCC !important;
  }
  
  .comment.topcomment footer {
    color: #808080 !important;
  }
  
  .subcomments {
    background-color: #1C1C1C !important;
  }
  
  .subcomments .toggle-show {
    color: green !important;
    background-color: black !important;
  }
  
}

@-moz-document domain("www.dumpert.nl") {

  body {
    background-image: none;
    background-color: black;
  }
  
  h1 {
    color: #56af24 !important;
  }
  
  p {
    color: white !important;;
  }
  
  input {
    background-color: #2A2A2A !important;
    color: grey !important;
    border: none !important;
  }
  
  textarea {
    background-color: #2A2A2A !important;
    color: grey !important;
    border: none !important;
  }
  
  article {
    border-color: black !important;
    border-bottom-color: #2A2A2A !important;
    background-image: none !important;;
  	background-color: black !important;
  }
  
  #top5 .dumpthumb {
    background: none !important;
  }
  
  #top5>h1 {
    color: black !important;
    background-color: #56af24 !important;
  }  
    
  #fancyupload {
    background-image: none !important;
    background-color: black !important;
    color: grey !important;
  }
  
  #upload_naam, #upload_email, #upload_title, #upload_description {
    border-color: black !important;
    margin-bottom: 1px !important;
  }

  .dump-cnt {
    color: white !important;
    background-color: black !important;
  }
  
  .dumpthumb {
    background-image: none !important;
    background-color: #1C1C1C !important;
    border-color: black !important;
  }
  
  .dumpthumb:hover {
    border-bottom-color: grey !important;
  }
  
  .dump-ftr, .dump-pan, .pagecontainer, .uiFile {
    background-color: black !important;
  }
  
  .file_item {
    color: white;
    background-color: black !important;
  }
  
  .uiDescription {
    background-color: #2A2A2A !important;
  }
  
  .dump-kudos, .dump-views>div, .dump-reactions>div {
    background-color: black !important;
    border-color: #2A2A2A !important;
  }
  
  .file_item {
    border: 1px solid #2A2A2A !important;
  }
  
}
