style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.gamekult.com") {
  
#main {
    background-color: #f8f8f8;
}
  
  #main-outlet {
    background-color: #fff;
}
  
  .topic-body {
    width: 700px;
}
  
  .timeline-container .topic-timeline{
    margin-left:0
  }
  
  div.post-stream {
    background: #fff;
}
  
  div.post-stream > div.topic-post:nth-of-type(2n+1) .topic-body {
    background: #fff;
}
  div.post-stream > div.topic-post:nth-of-type(2n+1) .topic-avatar {
    background-color: #fff;
}
  div.post-stream > div.topic-post:nth-of-type(2n+1) .topic-body::before {
    background-color: #fff;
}
  
  div.post-stream > div.topic-post:nth-of-type(2n) .topic-avatar {
    background-color: #fff;
}
  div.post-stream > div.topic-post:nth-of-type(2n) .topic-body {
    background: #fff;
}
  div.post-stream > div.topic-post:nth-of-type(2n) .topic-body::before {
    background-color: #fff;
}
  
  .topic-avatar,.topic-body{background-color:#fff; border-top:1px solid #e9e9e9 !important}
  
  .topic-post article.boxed{background-color:#fff}
  
  .topic-body:before{top:-1px !important;border-top:1px solid #e9e9e9 !important}

}
