style.css:
@-moz-document domain("editor.construct.net") {
ui-caption,
ui-dialog-caption {
	border-radius: 2px;
}
.eventSheetView {
	--event-sheet-background-color: #1b1b1b;
	background-color: var(--event-sheet-background-color);
}
.conditionBlock {
	background-color: #4c4c4c;
	border: 1px solid #0e0e0e;
}
.conditionObjectNameSpan,
.actionObjectNameSpan,
.actionDescCell {
	color: white;
}
.conditionDescCell {
	background-color: #616161;
	color: white;
}
.conditionFiller {
	background-color: #656565;
}
.actionDescCell,
.actionNameCell {
	background-color: #565656;
}
.addActionRow {
	background-color: #232323;
}
.ui-body {
	background-color: #252525;
}
span.tree-item-name {
	color: white;
}
.ui-tabbar {
	background-color: #232323;
}
#userAccountName {
	color: white;
}
#settingsDialog {
	background-color: #232323;
	color: white;
}
html[desktop] dialog[resizable]:not([inPopupWindow]) {
	border: 4px solid #464646;
}
div.propname-wrap {
	background-color: #313131;
	color: white;
}
ui-propvalue {
	border: 1px solid #676767;
	background-color: #252525;
	color: white;
}
ui-propname {
	background-color: #252525;
}
ui-propvalue>input {
	background: black;
	color: white;
}
a:not([href]) {
	color: #d2d2d2;
	text-decoration: underline;
	cursor: pointer;
}
}
