/*----------------------------------------------------------|Мини чат|----------------------------------------------------------------*/
.fc_msgs_out .fc_msgs .fc_msg.wrapped a {
    color: #e2e4e4;
}

  a.fc_msg_att_lnk {
        color: #adadad!important;
}
.fc_tab_head,
.fc_tab {
    background-color: #fff;
    border: solid #e7e8ec;
    border-width: 1px 1px 0px 1px;
    border-radius: 0px;
}
.fc_tab_head.clear_fix {
    height: 38px;
}
.fc_tab_title {
    color: #323435;
    height: 38px;
    line-height: 37px;
    font-size: 16px;
    font-weight: 400!important;
}
.fc_tab_typing {
    background-color: #fff;
}
a.fc_msg_att_lnk {
    color: #fff;
}
.fc_msgs {
    border-radius: 0px!important;
    padding: 8px 0px!important;
}
div.fc_msgs_author {
    display: none!important;
}
.fc_msgs_out .fc_msgs {
    background-color: #323232;
    border: 1px solid #323232!important;
    color: #fff;
}
.fc_msgs {
    background-color: #EFEFEF;
    border-color: #EFEFEF;
}
.fc_photo_thumb,
.fc_video_thumb {
    border-radius: 0px;
    margin-bottom: 3px;
}
.fc_msgs_unread {
    background-color: #fff;
}
.fc_msgs_unread:before {
    content: '';
    position: absolute;
    top: 4px;
    right: 12px;
    width: 5px;
    height: 5px;
    background-color: #fff;
    animation: mess 5s linear infinite;
}
@keyframes mess {
    0% {
        opacity: 0;
    }
    50% {
        opacity: 1;
    }
    100% {
        opacity: 0;
    }
}
.fc_msg_att_icon_mail,
.fc_msg_att_icon_wall {
    filter: grayscale(1);
}
