style.css:
@-moz-document domain("www.edmodo.com") {
#topbar,
.groups-container {
    background-color: #022d72;
    color: Black;
    border: none;
    box-shadow: 10px 10px 5px #0b1126;
}
body {
    background-color: #00013d !important;
    color: #00013d;
}
.span9 {
    display: block;
}
.inner-panel {
    background-color: #00013d;
}
#notifications-list {
    background-color: #00013d;
    color: black !important;
}
#notifications-list p {
    color: black !important;
}
.notification-reply-list,
.notification-reply-container,
.notification.new,
.notification.unread {
    background-color: #00013d !important;
}
.panel-title p {
    color: black;
}
.notification .subtext strong {
    color: blue;
}
#component-app-quicklaunch,
#component-app-quicklaunch .apps-dock ul li,
.section-header-bg-white,
.pb-content,
.stream-content-header,
.sub-section-header {
    background-color: #42579b !important;
    color: white !important;
}
.group-message-count,
.link-list-group-threaded>li>div>a,
.link-list-group-options li a,
.section-header-bg-white {
    color: white !important;
}
.sunken {
    background-color: inherit;
}

@media print {
    #topbar {
		display: none;
    }
}
}
