@keyframes show{
  0%{
    opacity:0;
    transform:scale3d(0, 0, 0); }
  to{
    opacity:1;
    transform:scaleX(1); } }

@keyframes horizontal-scroll{
  0%{
    transform:translate3d(100%, 0, 0); }
  to{
    transform:translate3d(-100%, 0, 0); } }

@keyframes ripple-darker{
  0%{
    background-color:rgba(0, 0, 0, .1);
    border-radius:100%;
    height:1px;
    width:1px; }
  to{
    background-color:rgba(0, 0, 0, .1);
    border-radius:0;
    height:5000%;
    width:5000%; } }

@keyframes ripple-lighten{
  0%{
    background-color:hsla(0, 0%, 100%, .1);
    border-radius:100%;
    height:1px;
    width:1px; }
  to{
    background-color:hsla(0, 0%, 100%, .1);
    border-radius:0;
    height:5000%;
    width:5000%; } }

@keyframes spinnerRotate{
  0%{
    transform:rotate(0deg); }
  to{
    transform:rotate(1turn); } }

@keyframes spinnerRotate2{
  0%,25%,45%{
    transform:rotate(0deg); }
  to{
    transform:rotate(1turn); } }

:root{
  --background-color:#eceff1;
