style.css:
@-moz-document domain("192.168.0.1") {
/* 隱藏的項目 */
.bannerhead,
.bannertail,
.footermark,
.footercontainer,
.copyright,
#help_hint{
	display:none;
}
/* Banner背景替換 */
.headercontainer {
    background-color: rgba(0, 0, 0, 0.5)   !important;
}
.bannercontainer{
	background: rgba(0, 0, 0, 0)   !important;
	height: 300px !important;
}
/* 取消邊框 */
.maincontainer {
    border-left: 0 !important;
    border-right: 0 !important;
}
/* 圓角 */
.maincontainer{
    border-radius: 10px;
	box-shadow: 0 2px 2px 0 rgba(0,0,0,0.16), 0 0 0 1px rgba(0,0,0,0.08);
}
.footercontainer{
	border-bottom-left-radius: 10px;
	border-bottom-right-radius: 10px;
}
.headercontainer{
	border-top-left-radius: 10px;
	border-top-right-radius: 10px;
}
/* 外距調整 */
.maincontainer{
	margin-top: 10px;
}
/* 登入視窗 */
#login > .simplebody > .orangebox > .message {
	display:none;
}
#login{
    background-color: rgba(0, 0, 0, 0)  !important;
	padding-top: 200px;
	height: 0px !important;
}
#login > .simplebody{
	width: auto!important;
}
#login > .simplebody > .orangebox h1{
	background-color: rgba(255, 255, 255, 0);
	font-size: 36px;
	font-weight: 500;
	height: auto;
	text-align:center;
	color: #FFF;
}
#login > .simplebody > .orangebox{
	background-color: rgba(0, 0, 0, 0.39);
	border-width: 0;
	margin-top: -300px;
}
#login > .simplebody > .orangebox > .loginbox .name{
	font-weight: 500;
	color: #FFF;
}
#login > .simplebody > .orangebox > .loginbox .delimiter{
	font-weight: 500;
	color: #FFF;
}
/* NAV */
.topmenucontainer .modelname{
	border: 0  !important;
	background: rgba(0, 0, 0, 0)!important;
	color: #FFF !important;
}
.leftmenu{
	background: rgba(0,0,0,.32)  !important;
}
.complexcontainer .leftmenu li{
	border-bottom: 4px solid rgba(0,0,0,0)!important;
    transition: all .1s cubic-bezier(0, 0.59, 0.99, 0.04);
}
.complexcontainer .leftmenu li:hover{
	border-bottom: 4px solid rgb(173, 51, 51) !important;
}
.complexcontainer .leftmenu li a{
    transition: all .1s cubic-bezier(0, 0.59, 0.99, 0.04);
}
.complexcontainer .leftmenu li a:hover{
	color: #FFF !important;
	background-color: rgba(0,0,0,0)!important;
	text-decoration: none !important;
}
#menu{
	background-color: rgba(0, 0, 0, .75);
	height: 32px !important;
}
div.topmenucontainer a:visited,
div.topmenucontainer a{
    border: 2px solid  rgba(0, 0, 0, 0)!important;
    color: #F1F1F1;
    background-color: rgba(0, 0, 0, 0)!important;
    transition: all .1s cubic-bezier(0, 0.59, 0.99, 0.04);
}
div.topmenucontainer a:hover{
    background-color: rgba(0, 0, 0,.8)!important;
}
div#menu a.label{
	color: #96ee00;
}
/* 設定頁 */
div.complexcontainer div.mainbody{
	width: 695px;
	border-top:  0;
    border-bottom: 0;
    border-left:0;
    border-right:0;
	padding: 5px;
	background-color: rgba(0,0,0,.32)  !important;
}
div.complexcontainer{
	border-top-color: rgba(0,0,0,0) !important;
}
.mainbody .orangebox,
#rmd,
#mbox>.simplebody .orangebox,
.blackbox,
.blackbox  .strong,
.blackbox  .name,
.blackbox .delimiter,
strong{
	border-width: 0px  !important;
	background-color: rgba(0,0,0,0) ;
	font-size: 14px  !important;
	color: #f7f2f2 !important;
	font-weight: 500  !important;
}
.mainbody .orangebox h1,
.mainbody .blackbox h2,
#mbox>.simplebody .orangebox h1,
#mainform>.blackbox h2,
#stage_ether>.blackbox h2,
#PPPoE>.blackbox h2{
	border-width: 0px  !important;
	background-color: rgba(0,0,0,0)  !important;
	font-size: 28px  !important;
	font-weight: 500  !important;
	height: 30px !important;
	border-bottom: 1px solid #f7f2f2 !important;
	color: #FFF !important;
}
#PPPoE>.blackbox{
	border-style: none !important;
}
#content.simplecontainer{
	    background-color: rgba(0, 0, 0, 0.59);
}
/* 輸入框 */
input, select, textarea{
	font-size: 14px;
}
input[type*="button"]{
	background-color: rgba(0,0,0,.5)  !important;
	color: #f7f2f2 !important;
	border: 1px solid #355609 !important;
	height: 30px !important;
    transition: all .1s cubic-bezier(0, 0.59, 0.99, 0.04);
	outline:none;
}
input[type*="button"]:hover{
	background-color: rgba(0,0,0,.7)  !important;
	border: 1px solid #528311 !important;
	color: #fff !important;
}
input[type*="button"]:active{
	background-color: rgba(0,0,0,.9)  !important;
	color: #fff !important;
	border: 1px solid #71b31a !important;
}
input[type=checkbox],
input[type=radio],
.name input[type=radio]{
	-webkit-appearance: none;
	appearance: none;
	width: 13px;
	height: 13px;
	margin-bottom: 9px !important;
	cursor: pointer;
	vertical-align: bottom;
	background-color: #fff !important;
	border: 1px solid #dcdcdc;
	-webkit-border-radius: 1px;
	-moz-border-radius: 1px;
	border-radius: 1em;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
	position: relative;
}

input[type=checkbox]:active,
input[type=radio]:active {
	border-color: #c6c6c6;
	background: #ebebeb;
}

input[type=checkbox]:hover {
	border-color: #c6c6c6;
	-webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,0.1);
	-moz-box-shadow: inset 0 1px 1px rgba(0,0,0,0.1);
	box-shadow: inset 0 1px 1px rgba(0,0,0,0.1);
}

input[type=radio] {
	-webkit-border-radius: 1em;
	-moz-border-radius: 1em;
	border-radius: 1em;
	width: 15px;
	height: 15px;
}

input[type=checkbox]:checked,
  input[type=radio]:checked {
	background: #fff;
}

input[type=radio]:checked::after {
	content: '●';
	display: block;
	position: relative;
	top: -3px;
	left:  2px;
	color: #666;
}

input[type=checkbox]:checked::after {
	content: url(https://ssl.gstatic.com/ui/v1/menu/checkmark.png);
	display: block;
	position: absolute;
	top: -6px;
	left: -5px;
}

input[type=checkbox]:focus {
	outline: none;
	border-color: #4d90fe;
}
/* 登出 */
#mbox>div.simplecontainer{
	background-color: rgba(240, 240, 240, 0);
	margin-top: -40px  !important;
}
#mbox{
	background-color: rgba(31, 10, 10, 0.52) !important;	
	border-bottom-left-radius: 10px;
	border-bottom-right-radius: 10px;
}
/* 表格 */
div.blackbox table.general{
	width: 690px;
	background-color: #dfdfdf;
}
div.blackbox table.general tr td{
	background-color: #ffffff;
	color: #000 !important;
}
div.blackbox table.general tr th{
	background-color: #8a5454;
}
/* 卷軸樣式 */
::-webkit-scrollbar-track
{
	background-color: #F5F5F5;
}

::-webkit-scrollbar
{
	width: 5px;
	background-color: #F5F5F5;
}

::-webkit-scrollbar-thumb
{
	box-shadow: inset 0 0 6px rgba(0,0,0,.3);
	background-color: #555;
}
}

