@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wordpress.org") {

/** General */

/* .home .site-main section:first-child {
  display: none !important;
} */

/* html[lang="en-US"] .home .site-main section:nth-child(2) {
  display: none;
} */

html[lang="de-DE"] .home .site-main section:nth-child(2) {
  display: none;
}

/* html[lang="en-US"] */
html[lang="en-US"] .home .site-main section:nth-child(3) {
  display: none !important;
}

.locale-banner {
  display: none !important;
}

nav .nav-links .page-numbers.current {
  background-color: #ffffcc !important;
  border: 1px solid #0073aa !important;
  color: #0073aa !important;
}

nav .nav-links .page-numbers.dots {
  background-color: transparent !important;
  border: none !important;
  color: #0073aa !important;
}
  
nav .nav-links .page-numbers,
nav .nav-links .page-numbers.next,
nav .nav-links .page-numbers.prev {
  background-color: #0073aa !important;
}

nav .nav-links a.page-numbers {
  color: #fff;
  margin-left: 7px;
  margin-right: 7px;
  padding-left: 15px;
  padding-right: 15px;
  text-decoration: none;
}

nav .nav-links .page-numbers.next {
  margin-left: 40px;
}

nav .nav-links .page-numbers.prev {
  margin-right: 40px;
}

nav .nav-links .page-numbers.next,
nav .nav-links .page-numbers.prev {
  padding-left: 15px;
  padding-right: 15px;
}


nav .nav-links .page-numbers.next,
nav .nav-links .page-numbers.prev {
 position: relative;
}

nav .nav-links .page-numbers.next:after {
 left: 100%;
 top: 50%;
 border: solid transparent;
 content: " ";
 height: 0;
 width: 0;
 position: absolute;
 pointer-events: none;
 border-color: rgba(0, 115, 170, 0);
 border-left-color: #0073aa;
 border-width: 20px;
 margin-top: -20px;
}

nav .nav-links .page-numbers.prev:after {
 right: 100%;
 top: 50%;
   border: solid transparent;
 content: " ";
 height: 0;
 width: 0;
 position: absolute;
 pointer-events: none;
 border-color: rgba(0, 115, 170, 0);
 border-right-color: #0073aa;
 border-width: 20px;
 margin-top: -20px;
}

nav .nav-links .page-numbers.current:hover {
  text-decoration: none;
}

nav .nav-links .page-numbers:hover,
nav .nav-links .page-numbers.next:hover,
nav .nav-links .page-numbers.prev:hover {
  text-decoration: underline;
}

.plugin-section {
  border-bottom: none;
  margin: 0 auto 1.5rem;
}

.plugin-section .section-header {
  background-color: #ffffdd;
  margin-bottom: 20px;
  padding: 20px;
}

.plugin-section .section-title {
  margin: 0;
  padding: 0;
}

.plugin-section .section-link {
  right: 20px;
  top: 30px;
}

.plugin-section .section-link:after {
  content: '→';
  padding-left: 5px;
}

.archive .page-header,
.search .page-header {
  background-color: #ffffdd;
  padding: 20px;
}

.archive .page-header p,
.search .page-header p {
  margin: 1em 0 10px;
}

.site-main .page-title {
  margin: 0;
}


/** Single Plugins */

.type-plugin .plugin-notice {
  line-height: 1.7;
  margin-bottom: 40px;
  padding: 10px 20px;
}

.type-plugin .plugin-header {
  border-bottom: none;
}

.type-plugin .tabs,
.type-plugin .entry-content {
  width: 70%;
}

.type-plugin .entry-meta {
  width: 25%;
}

/*
.single-plugin .plugin-title a {
  display: inline-block;
  margin-right: 20px;
  max-width: 48%;
}
*/

.section.read-more {
  border-bottom: none;
}

.single-plugin .entry-content .section,
.single-plugin .plugin-screenshots {
  background-color: #f7f7f7;
  margin-bottom: 40px;
  padding: 20px;
}

.single-plugin .section h2,
.plugin-screenshots h2 {
  background-color: #ffffcc;
  padding: 20px !important;
}

.single-plugin .section h2,
.plugin-screenshots h2 {
  margin-left: -20px;
  margin-right: -20px;
  margin-top: -20px;
}



.single-plugin .section-toggle {
  background-color: #ffffcc;
  display: none !important;
  margin-bottom: 40px;
  padding: 20px;
  position: inherit;
  width: 100%;
}

.single-plugin .section.read-more,
.single-plugin .section.read-more.plugin-description {
  max-height: 100% !important;
}

.plugin-description.section h3,
.type-plugin .section h4,
.type-plugin .section h5,
.type-plugin .section h6 {
  margin-left: -20px;
  margin-right: -20px;
}

.plugin-description.section h3 {
  background-color: #e5f5fa;
  padding: 20px;
}

.type-plugin .section h4,
.type-plugin .section h5 {
  background-color: #ddd;
  padding: 10px 20px;
}

.plugin-description.section blockquote > h3,
.plugin-description.section blockquote > h4,
.plugin-description.section blockquote > h5,
.plugin-description.section blockquote > h6 {
  margin-right: inherit;
}

.plugin-changelog.section h4:nth-child(2),
#admin.section h4:nth-child(2) {
  margin-top: -20px !important;
}

.plugin-changelog ul {
  list-style: square;
  margin-bottom: 40px;
  margin-left: 20px;
  margin-top: -10px;
}

.plugin-changelog ul ul {
  list-style: circle;
  margin-bottom: 20px;
  margin-left: 40px;
  margin-top: 5px;
}

.plugin-faq h2:after {
  margin-left: 7px;
}

html[lang="en-US"] .plugin-faq h2:after,
html[lang="en-GB"] .plugin-faq h2:after,
html[lang="en-AU"] .plugin-faq h2:after,
html[lang="en-NZ"] .plugin-faq h2:after,
html[lang="en-CA"] .plugin-faq h2:after {
  content: '–  Frequently Asked Questions';
}

html[lang="de-DE"] .plugin-faq h2:after,
html[lang="de-AT"] .plugin-faq h2:after,
html[lang="de-CH"] .plugin-faq h2:after {
  content: '–  Häufig gestellte Fragen';
}

.plugin-faq dl {
  border-bottom: none;
  margin-top: -20px;
}

/* .plugin-faq dl:not(:nth-child(1)) {
  padding-top: 100px;
} */

.single-plugin .plugin-faq > p:nth-last-of-type() {
  display: block;
  /* margin-bottom: 30px !important; */
  padding-bottom: 100px !important;
}

.plugin-faq dt {
  background-color: #dfdfdf;
  /* padding: 0.3rem 0 0.3rem 0.5rem; */
  margin-left: -20px;
  margin-right: -20px;
  padding: 0.4rem 20px 0.4rem 20px;
}

.plugin-faq dt.open {
  margin-bottom: 20px;
}

.plugin-faq dt.open:last-child {
  margin-bottom: 0;
}

.plugin-faq dd {
  display: block;
  margin: 0 0 2rem;
  padding-top: 0.4rem;
}

.plugin-faq dd:last-child {
  margin-bottom: 0;
  padding-bottom: 10px;
}

.plugin-faq dt > a {
  text-decoration: none !important;
}
  
.plugin-faq dt::before {
  display: none;
}

.plugin-changelog h2:after {
  margin-left: 7px;
}

html[lang="en-US"] .plugin-changelog h2:after,
html[lang="en-GB"] .plugin-changelog h2:after,
html[lang="en-AU"] .plugin-changelog h2:after,
html[lang="en-NZ"] .plugin-changelog h2:after,
html[lang="en-CA"] .plugin-changelog h2:after {
  content: '–  Version History';
}

html[lang="de-DE"] .plugin-changelog h2:after,
html[lang="de-AT"] .plugin-changelog h2:after,
html[lang="de-CH"] .plugin-changelog h2:after {
  content: '–  Versionshistorie';
}

.type-plugin .entry-content pre {
  background-color: #fff;
  border-radius: 5px;
}

.type-plugin .entry-content pre,
.type-plugin .entry-content code {
  color: #777;
}


/** Screenshot Gallery */

.image-gallery-slides {
  border: 1px solid #0073AA;
}

.image-gallery-slide .image-gallery-description {
  background-color: #0073AA;
  color: #fafafa;
}

.plugin-screenshots .image-gallery-thumbnails:before {
  content: 'Browse Images:';
  display: block;
  font-size: 95%;
  font-style: italic;
  margin-top: 15px;
  margin-bottom: 8px;
}

html[lang="en-US"] .plugin-screenshots .image-gallery-thumbnails:before {
  content: 'Browse Images:';
}

html[lang="de-DE"] .plugin-screenshots .image-gallery-thumbnails:before {
  content: 'Screenshot-Galerie durchblättern:';
}

.image-gallery-thumbnails {
  background-color: inherit;
  /* margin-top: 15px; */
}

.image-gallery-thumbnail.active {
  border: 5px solid #337ab7;
}

.image-gallery-thumbnail {
  border: 5px solid #ccc;
}

.image-gallery-thumbnail.active:hover {
  border: 5px solid #337ab7;
  opacity: .65;
}

.image-gallery-thumbnail:hover {
  border: 5px solid #ffffee;
}

.image-gallery-thumbnail:focus {
  border: 5px solid #ffffcc;
}

.image-gallery-content .image-gallery-left-nav::before,
.image-gallery-content .image-gallery-right-nav::before {
  color: #000;
}

.image-gallery-content .image-gallery-left-nav:hover::before,
.image-gallery-content .image-gallery-right-nav:hover::before {
  color: #fff;
}

.image-gallery-content .image-gallery-left-nav:hover,
.image-gallery-content .image-gallery-right-nav:hover {
  background: #0073AA;
  opacity: .5;
}


/** Plugin Meta */

.type-plugin .plugin-title {
  margin-right: 320px !important;  /* 290 */
}

.rtl .type-plugin .plugin-title {
  margin-left: 320px;  /* 290 */
}
  
.plugin-header {
  margin-bottom: 30px;
  margin-top: 20px;
}

.plugin-header .byline {
  display: block;
  margin-top: 10px;
}

/* Jetpack special */
.post-85 .byline:after {
  color: red;
  content: " // Caution: contains paid services! ;-)";
  display: inline-block;
  margin-left: 10px;
}

html[lang="de-DE"] .post-85 .byline:after,
html[lang="de-AT"] .post-85 .byline:after,
html[lang="de-CH"] .post-85 .byline:after {
  content: " // Achtung: enthält kostenpflichtige Dienste! ;-)";
}

.plugin-meta {
    margin-top: 22px;
    margin-top: 1.2rem;
}

.plugin-meta ul:before {
  clear: both;
  content: 'Plugin Info';
  display: block;
  font-family: inherit;
  line-height: 1.5;
  margin: 0 0 16px;
  margin: 0 0 1rem;
  
  font-size: 20px;
  font-size: 1.25rem;
  border: none;
  color: #32373c;
  font-weight: 600;
  padding: 0;
}

.plugin-meta ul li:first-child {
  border-top: none;
}

.plugin-meta ul li:nth-child(4) strong:before,
.plugin-meta ul li:nth-child(5) strong:before {
  color: #aaa;
  content: ' ';
  display: inline-block;
  font-family: 'dashicons';
  font-weight: 300 !important;
  margin-right: 7px;
  vertical-align: bottom;
}

.plugin-meta .tags {
  clear: right;
  display: block;
  margin-top: 10px;
  width: 100% !important;
}

html[lang="en-US"] .plugin-meta .tags {
  display: inline-block;
  margin-top: 0;
  width: 80% !important;
}

.plugin-meta [rel="tag"]:hover {
  background: #ffffdd;
}

.plugin-meta .plugin-admin:after {
  content: '→';
  padding-left: 5px;
}

.user-rating,
.plugin-support p:nth-child(4),
.plugin-support p:nth-child(3),
.plugin-donate p:nth-child(3) {
  text-align: right;
}

.plugin-support .counter-count {
  padding-right: 7px;
}

html[lang="de-DE"] .plugin-ratings .reviews-link,
html[lang="de-AT"] .plugin-ratings .reviews-link,
html[lang="de-CH"] .plugin-ratings .reviews-link {
  display: block;
  margin-bottom: 10px;
  margin-top: -15px;
  position: relative !important;
  text-align: right;
}

.download-button.button-large {
  font-size: 120%;
  padding: 0.5rem 2rem !important;
  height: auto !important;
}

.download-button.button-large:before {
  color: #f5f5f5;
  content: ' ';
  display: inline-block;
  font-family: 'dashicons';
  font-weight: 300 !important;
  margin-right: 10px;
  vertical-align: bottom;
}

.plugin-favorite .plugin-favorite-heart {
  font-size: 3rem !important;
  margin-right: 150px !important;
  margin-top: 10px;
}


/** Single Plugin Advanced */

table.download-history-stats {
  margin-right: 80px;
  width: 66%;
}

/*table.download-history-stats tr:first-child,*/
table.download-history-stats tr:nth-child(2n+1) {
  background-color: #ddd;
}

table.download-history-stats th,
table.download-history-stats td {
  padding: 10px;
}

.plugin-contributors.read-more {
  border-bottom: none;
  overflow: visible;
}


/** Plugin Cards */

.plugin-section .plugin-card.post-113,
.plugin-section .plugin-card.post-85,
.plugin-section .plugin-card.post-70455 {
  display: none;
}

article.plugin-card {
  border: 1px solid #fff;
}
  
article.plugin-card:focus,
article.plugin-card:hover {
  background-color: #ffffcc;
  border-color: #5b9dd9;
  box-shadow: 0 0 2px rgba(30,140,190,.8);
}
  
.plugin-card footer {
  background-color: #eee;
  display: block;
  width: 100%;
}

article.plugin-card:focus footer,
article.plugin-card:hover footer {
  background-color: #e1e1e1;
}

.plugin-card hr {
  display: none !important;
}

.plugin-card {
  padding: 0 !important;
}

.plugin-card .entry-thumbnail,
.plugin-card .entry {
  padding: 20px;
}

.plugin-card .entry-title {
 background-color: #0072ac;
 display: block;
  font-weight: 600;
 /* margin-left: -18px; */
 margin-right: -20px;
 padding: 10px 10px 10px 20px;
}

.plugin-card .entry-title a {
  color: #f5f5f5;
}

.plugin-card footer {
  padding: 15px 20px 10px;
}


/** Plugin Reviews & Support */

.wporg-support .entry-content,
.bbpress main#main > .entry-content,
.page-template-page-forums-sidebar main#main > .entry-content {
  width: 70%;
}

.wporg-support .entry-meta,
.bbpress main#main > .entry-meta,
.page-template-page-forums-sidebar main#main > .entry-meta {
  width: 25%;
}

.entry-meta.sidebar ul li:first-child {
  border-top: none;
}

.entry-meta.sidebar .plugin-icon {
  margin-bottom: 15px !important;
}

#bbpress-forums div.even:hover,
#bbpress-forums ul.even:hover,
#bbpress-forums div.odd:hover,
#bbpress-forums ul.odd:hover {
  /* opacity: .85; */
  background-color: #ddd;
}

.bbp-forum-content ul.sticky:hover,
.bbp-topics ul.sticky:hover,
.bbp-topics ul.super-sticky:hover,
.bbp-topics-front ul.super-sticky:hover {
  background-color: #ffffcc !important;
}


/** Member Profiles */

.xprofile #headline {
  background-color: #0072ac;
  border-bottom: none;
}

.xprofile #headline h2 {
  color: #f5f5f5;
  letter-spacing: 1px;
  text-shadow: none;
}

.xprofile #headline .login {
  color: #ccc;
}

.xprofile #headline .login a {
  color: #eee;
}

.xprofile #headline .login a:hover {
  text-decoration: underline;
}

.xprofile #item-header {
  background-color: #ffffcc;
}

div#item-header h2 {
  display: inline-block;
  margin-top: 30px;
  /* padding-left: 25px; */
}

div#item-header img.avatar {
    float: left;
    margin: 0 20px 12px 0;
}

#header-meta-links {
    padding-right: 20px;
}

.xprofile #meta-status-badge-container {
  background-color: #e5f5fa;  /* #f5f5f5; */
  margin-bottom: 0;
  margin-top: 30px;
  padding: 30px 20px 20px;
}

.xprofile #user-meta {
    background-color: rgba(221, 229, 250, 0.75);
    margin-right: -20px;
    padding: 20px;
    /* width: 100%; */
    /* max-width: 410px; */
}
  
#user-meta .dashicons,
#user-meta .genericon,
#user-social-media-accounts-tag,
#content-about h4,
ul#activity-list li:before,
#content-favorites .favorites h3 {
  color: #0072ac;
}

/*
.xprofile div#item-header #user-badges {
  width: 100%;
  max-width: 310px;
  height: 150px;
}
*/

.xprofile #user-social-media-accounts li {
  margin-right: 15px;
}

#header-meta-links {
  font-size: 13px;
}

.xprofile #item-body {
  margin-top: 60px;
}
  
#content-tabs {
    border-top: none;
}

#content-tabs.info-group {
  font-size: 13px;
}
  
#content-tabs ul li.active {
  background-color: #f5f5f5;
  border-right-color: #f5f5f5;
}

#main-column {
  background-color: #f5f5f5;
  margin-bottom: 40px;
}

#content-tabs ul li:hover {
  background-color: #ccc;
}

#content-tabs ul li.active::before,
#content-tabs ul li.active::after {
    display: none;
}
  
li.wporgactivity a,
li.tracplugins a,
li.tracthemes a,
li.traccore a,
li.tracmeta a,
li.tracbbpress a {
	background-color: #ffffcc;
    font-weight: 700;
    padding: 2px 3px;
}

ul#activity-list li {
	padding: 10px 20px 10px 40px;
}

ul#activity-list li:nth-child(odd) {
	background-color: inherit;
}
 
ul#activity-list li:nth-child(even) {
	background-color: #eee;
}

ul#activity-list li:hover,
ul#activity-list li:hover:nth-child(even) {
	background-color: #ddd;
}
  
.main-plugins ul li:hover,
.main-themes ul li:hover {
	background-color: #ddd;
}
  
.main-themes ul li {
	display: block !important;
  padding: 10px 40px !important;
}

.main-themes h3 {
	font-size: 18px !important;
}

.main-themes .active_installs {
	font-size: 15px !important;
}


/** Themes / Theme Browser */

.wp-filter {
  padding: 10px 20px 0;
}

.wp-filter .search-form {
  margin: 3px 0 10px;
}

.wp-filter .wp-filter-search {
  background-color: #ffffcc;
  padding: 6px 10px;
}

.filter-links,
.filter-links li {
  font-size: 14px;
}

.theme-browser .theme:hover .entry-title {
  background-color: #ffffcc;
}

.theme-navigation {
  background-color: #e5f5fa;
}
  
.theme-navigation .close {
  font-size: 16px;
} 

.theme-navigation .left,
.theme-navigation .right {
  background-color: #0073aa;
}

.theme-navigation .left:before,
.theme-navigation .right:before {
  color: #fff;
}

.theme-navigation .left:hover:before,
.theme-navigation .right:hover:before,
.theme-navigation .left.disabled:before,
.theme-navigation .right.disabled:before {
  color: #0073aa;
}

.theme-about.hentry > div:first-child {
  background-color: #ffffcc;
  margin-bottom: 40px;
  margin-top: 20px;
  padding: 15px 20px 0;
}

.theme-wrap .theme-author {
  margin-left: 20px;
}

.theme-tags h4,
.theme-downloads h4,
.theme-meta h4 {
  background-color: #e5f5fa;
  padding: 10px 20px;
}
  
.theme-wrap .theme-description {
  font-size: 17px;
}

.theme-meta a.button {
  display: block;
  text-align: center;
}

.theme-ratings .reviews-link {
    top: 10px;
}

.theme-downloads {
  margin-bottom: 30px;
}

.theme-translations a {
    border: 1px solid;
    border-radius: 3px;
    box-sizing: border-box;
    cursor: pointer;
    display: block;
    font-size: 12.8px;
    font-size: .8rem;
    height: 25px;
    height: 1.5625rem;
    line-height: 25px;
    line-height: 1.5625;
    margin: 0;
    padding: 0 12.8px;
    padding: 0 .8rem;
    text-align: center;
    text-decoration: none;
    white-space: nowrap;
    -webkit-appearance: none;
}

.theme-translations a,
.theme-translations a:visited {
    background: #f7f7f7;
    border-color: #ccc;
    box-shadow: 0 1px 0 #ccc;
    color: #555;
    vertical-align: top;
}

.theme-translations a:active,
.theme-translations a:focus,
.theme-translations a:hover {
    text-decoration: none;
}

.theme-translations a:focus,
.theme-translations a:hover {
    background: #fafafa;
    border-color: #999;
    color: #23282d;
}

.theme-translations a:active {
    background: #eee;
    border-color: #999;
    box-shadow: inset 0 2px 5px -3px rgba(0,0,0,.5);
    transform: translateY(1px);
}

.theme-translations a:focus {
    border-color: #5b9dd9;
    box-shadow: 0 0 3px rgba(0,115,170,.8);
}

  
/* Themes: Main Navigation */

#themes.wrap {
  margin-top: 20px;
}

#menu-theme-directory.menu {
  padding: 20px 0;
}


/** WordPress.org Homepage */
  
#home-welcome .promo,
#lang-guess-wrap {
  display: none;
}


/** Help Hub */

.single-helphub_article.wporg-support .entry-content {
    width: inherit;
}
  

@media only screen and (max-width: 940px) {
  
  /* .single-plugin .plugin-title a {
    max-width: 480px;
  } */
  
}


@media only screen and (max-width: 767px) {

  .type-plugin .tabs,
  .type-plugin .entry-content,
  .type-plugin .entry-meta {
    clear: both;
    float: none;
    width: 100%;
  }
  
  .type-plugin .tabs {
    padding: 0 1.5625rem !important;
  }
  
  .entry-thumbnail {
    clear: both !important;
    float: none !important;
  }
  
  .plugin-actions {
    margin-top: -70px;
  }
  
