style.css:
@-moz-document domain("www.mediavida.com") {
/*Color de fondo*/
#content {
    background-color: hsl(0, 0%, 13%);
}

/*Cabecera naranja: naranja plano*/
header#title {
    background: #ec7309;
    position: relative;
    border-bottom: none!important;
    border-top: none!important;
    z-index: 0;
    height: 120px;
}

/*Botones general*/
.btn {
    border-radius: 0px;
    border: 0px solid black;
    box-shadow: none;
}

.fa-close::before, .fa-remove::before, .fa-times::before {
    /*content: "";*/
    content: "\f068";
    font-size: 12px;
    background: red;
    padding: 2px 4px;
    border-radius: 100px;
    color: white;
    border: 1px solid red;
    line-height: 2;
    opacity: 0.5;
}

.fa-close::before:hover, .fa-remove::before:hover, .fa-times::before:hover {
    opacity: 1;
}

/*Cambio de colores de botones en header naranja*/
.brand .btn-default {
    color: #35220d;
    background-color: #f5f2f0;
    border-color: #D5D3D0;
}

/*OPCIONAL Cambiar letra de todo el contenido
#content {
    background-color: hsl(0, 0%, 13%);
    font-family: sans-serif;
}*/
/*Bloque de posts: eliminación del borde*/
.block {
    /* border-style: solid; */
    border-width: 1px 0;
    /* border-color: #e9e7e5 #e4e2df #d5d3d0; */
}

.block {
    border-radius: 0px;
    border-color: transparent;
}

/*Primer post: separado*/
#posts-wrap > .cf.post.first.op {
    border-bottom: 20px solid #212121;
}

#post-1.cf.post.news.first.op .post-contents {
    max-width: 100%;
}

/*MARGEN DERECHO*/
/*Títulos secciones*/
.b-side h3 {
    border-top: rgba(154, 205, 50, 0);
    padding: 3px 0px 3px;
    text-transform: uppercase;
    background-color: #ec7309;
    border-radius: 100px;
    text-align: center;
    margin-bottom: 10px;
}

/*Margen del elemento acompañante (el que se mueve) en el margen derecho*/
#thread-companion {
    margin-bottom: 10px;
}

/*Margen de la barra de búsqueda*/
.b-side.b-search {
    margin-bottom: 0px
}

/*Color de texto en el margen derecho: blanco*/
.b-side h3,
a.side {
    color: #fffcfa;
    font-size: 15px;
}

#foros.foros .b-side h3,
a.side {
    margin-top: 25px;
}

/*Color días de calendario:blanco*/
.event-cal .day {
    color: #fbfaf9;
    font-size: 19px;
    line-height: 1;
}

/*Flecha de subir (top): color blanco*/
.fa-chevron-circle-up:before {
    content: "";
    color: #fbfaf9;
}

/*Destacados: color de letra blanco*/
.featured-entry a.title {
    margin-left: 34px;
    color: #fffcfa;
    display: block;
    font-size: 15px;
    line-height: 1.15;
}

/*Eventos: color de letra blanco*/
.event-desc a {
    color: #fffcfa;
    display: block;
    font-size: 15px;
}

#sections > li > a {
    height: 42px;
    line-height: 42px;
    float: left;
    padding: 0 14px;
    color: rgba(255, 255, 255, .65);
}

/*(SOLO YO) Header color notificación violeta
#usermenu .hl .bubble {
    display: inline-block;
    background-color: #b86ddd;
}*/
/*Usermenu: color de fondo negro*/
#usermenu {
    padding: 0 6px;
    background-color: #000000;
}

#usermenu .hl .bubble {
    border-radius: 100px;
}

#header {
    position: fixed;
    top: 0;
    width: 100%;
    max-width: 100%;
    height: 42px;
    line-height: 42px;
    background: #000000;
    border-bottom: 1px solid transparent;
    z-index: 90;
