/*Post quoteado: opacidad más alta*/
.post.quote-shown {
    /*-webkit-transition: opacity .5s ease-out!important*/;
    transition: opacity .5s ease-out!important;
    opacity: 0.9;
}

/*Fecha de post y lápiz de editado: fondo*/
.post-meta span.rd,
.post-meta time.edited,
.post-meta-reply span.rd,
.post-meta-reply time.edited {
    color: #9c9793;
    margin-right: 4px;
    border-radius: 100px;
    margin-left: 3px;
    margin-top: -3.2px;
    padding: 2.5px 8px;
}


/*FIRMAS*/
/*Mensajes más pegados*/
.firma {
    margin-bottom: 0px;
    border-radius: 0px!important;
}

#perfil.usuarios.cover .b-side {
    margin-top: 0px;
}

#perfil.usuarios.cover a.next-link {
    padding: 0px 4px;
    font-weight: 800;
    font-size: 15px;
    text-decoration: underline;
}

#perfil.usuarios.cover table.mv.full .unseen-num {
    padding: 4px 2px;
    border-radius: 20px;
    min-width: 18px;
    max-width: 0;
    line-height: 13px;
    position: relative;
    left: 0px;
    float: none;
    height: 100%;
    margin-bottom: 0px;
    margin-top: 0px;
}

#perfil.usuarios.cover .post-contents {
    max-height: 438px;
}

/*LETRAS EN FIRMAS*/
/*Letra de max 500 caracteres en firmas*/
#firmar .pull-left {
    float: left!important;
    color: #fffcfa;
}

/*Títulos*/
#perfil.usuarios.cover .row.cf h3 {
    font-size: 16px;
    color: #000;
    text-transform: uppercase;
    text-align: center;
    margin-bottom: 10px;
    background: #fffcfa;
    border-radius: 20px;
    font-weight: 500;
    padding: 1px 0px;
}

/*Número de visitas*/
.muted {
    color: #fffcfa;
    font-weight: 600;
}

/*LETRAS EN MENSAJES PRIVADOS*/
/*Título: usuario*/
.pm-title h1 {
    font-size: 18px;
    margin: 0;
    padding: 0;
    display: inline-block;
    color: #fbfaf9;
}

/*PANTALLA NOTIFICACIONES*/
/*Títulos*/
.items.notify h3 {
    font-size: 18px;
    color: #fffcfa;
}

/*SCROLLBAR*/
::-webkit-scrollbar {
    width: 17px;
    height: 12px;
}

::-webkit-scrollbar-track {
    background: #fbfaf9;
}

::-webkit-scrollbar-thumb {
    background: #7f7f7f;
}

/*Color de fondo de tema sin leer*/
#temas .unread .col-th {
    padding-left: 8px;
    background: linear-gradient(to left, #fbfaf9 0%, rgb(237, 241, 243) 100%) !important;
}

/*Título de hilos sin respuestas nuevas más claro*/
.thread a.h {
    color: #b1b1b1;
    display: inline-block;
    max-width: 415px;
    line-height: 16px;
}

/*Título de hilos sin leer o nuevos más oscuro*/
.thread a.hb {
    color: #262422;
    max-width: 370px;
    display: inline-block;
    line-height: 16px;
}

#foros_spy.foros .thread a.hb {
    max-width: 340px;
}

/*Color de fondo de tema cerrado*/
#temas tr.locked td.col-th {
    padding-left: 8px;
    background: linear-gradient(to left, #fbfaf9 0%, rgb(230, 230, 230) 100%)!important;
}


/*SPOILERS*/
/*Más bold*/
.post-contents a.spoiler {
    cursor: pointer;
    color: #545250;
    font-weight: 500;
    background: 0 0;
    padding: 0 1px;
}

/*Tipo de spoiler: cerrado*/
.post-contents a.spoiler:before {
    color: #ed540c;
    content: "\f138";
    display: inline-block;
    font: normal normal normal 14px/1 FontAwesome;
    text-rendering: auto;
    font-weight: 500;
    -webkit-font-smoothing: antialiased;
    margin-right: 3px;
    vertical-align: middle;
}

/*Tipo de spoiler: después*/
.post-contents a.spoiler.less:before {
    content: "\f139";
}

/*Número de páginas en hilo (bottom)*/
.pg > li > a:hover,
.pg > li > span.current {
    background-color: #2899b8;
    color: #fbfaf9!important;
}

.pg > li > a,
.pg > li > span {
