style.css:
@-moz-document url-prefix("http://www.sgtools.info") {
.featured__inner-wrap a img {
    display: block!important
}

a [src|="https://www.kinguin.net"],
[href|="https://www.kinguin.net"],
.center #leaderTop a img,
.center #leaderTop a href,
div #content .left-column a img,
div #content .left-column a href {
    display: none!important;
    height: 0!important;
    width: 0!important;
    filter: opacity(0%);
    pointer-events: none;
    cursor: default
}
}

@-moz-document url("http://www.sgtools.info/"), url("http://www.sgtools.info/activation"), url("http://www.sgtools.info/real-cv"), url("http://www.sgtools.info/sync"), url("http://www.sgtools.info/multiple-wins"), url("http://www.sgtools.info/giveaways/manage"){
div .center a img,
.center a,
.center a img {
    display: none!important;
    height: 0!important;
    width: 0!important;
    filter: opacity(0%);
    pointer-events: none;
    cursor: default
}
}

@-moz-document url("http://www.sgtools.info/lastbundled") {
div .center a img,
body div div #leaderTop,
.center a img {
    display: none!important;
    height: 0!important;
    width: 0!important;
    filter: opacity(0%);
    pointer-events: none;
    cursor: default
}

}
@-moz-document url("http://www.sgtools.info/whitelisted"), url("http://www.sgtools.info/giveaways/dashboard"){
body div div #leaderTop{
    display: none!important;
    height: 0!important;
    width: 0!important;
    filter: opacity(0%);
    pointer-events: none;
    cursor: default
}
}
