style.css:
@-moz-document domain("calendar.google.com") {
td span, h2 span
{
    color: white;
}
body
{
    background-image: url(/*[[customBack]]*/);
    position: relative
}
#gridcontainer 
{
    opacity: .9;
    filter: alpha(opacity=90);
    height: 300px;
    background: #fff;
    position: relative;
    -webkit-user-select: none
}
#dp_0.dpdiv 
{
    background-color: rgba(0, 0, 0, 0);
    padding: 0 0 15px
}
/* This is for the Mini calendar*/
.monthtableHeader:hover 
{
    background-color: #eee
}
.monthtableHeader:hover span
{
    color: black;
}
.calHeader 
{
    background: rgba(0, 0, 0, 0);
}
.calHeader:hover span
{
    color: black;
}
.dp-days
{
	background-color: white;  
}
.chromeColor 
{
    background: rgba(0, 0, 0, 0);
    border-color: #fff;
}

.label-input-label 
{
    color: #888
}

#searchAddCalBox 
{
    background: rgba(0, 0, 0, 0);
    padding: 3px 5px 2px 0px;
}
/*Navigation*/
#topLeftNavigation 
{
    padding-top: 10px;
    float: left;
    white-space: nowrap;
}
#topRightNavigation,#searchNavigation 
{
    padding-top: 10px;
    margin-right: 12px;
    position: absolute;
    right: 16px;
    top: 0;
    white-space: nowrap
}
#mainnav
{
    background: #fff;
    padding: 0px 0px 0px 0px;
    border-color: #e7ebef
}
#vr-nav 
{
    background: #fff;
    border-bottom: 1px solid #ebebeb;
    padding: 10px 10px 10px 10px;
    margin: 9px 0 0;
    position: relative
}
}
