style.css:
.c-side,
.c-side-alt,
.c-side-s,
.c-side-s-alt {
    background: #39444b;
}
.featured-entry a.title {
    color: #e9e9e9;
}
.unread-num,.unseen-num{
    font-size: 14px;
    padding: 10px 6px;
    border-radius: 3px;
    min-width: 18px;
    max-width: 48px;
    line-height: 1;
    margin-left: 8px;
    position: relative;
    left: -10px;
    float: left;
    height: 100%;
}
#usermenu .unread-num,#usermenu  .unseen-num{
    font-size: 14px;
    padding: 8px 4px;
    border-radius: 3px;
    min-width: 20px;
    max-width: 48px;
    line-height: 1;
    margin-left: 8px;
    position: relative;
    left: -10px;
    float: left;
    height: 100%;
}
.link-entry {
    margin-bottom: 3px;
    border-bottom: 1px #333 solid;
    padding-bottom: 2px;
}
._p-ft, ._p-tp{
	display:none;
}
a.side {
    color: #ccc;
}
.b-side h3 {
    border-top: 1px solid #343f48;
    color: white;
    font-size: 15px;
    padding: 2px 0 2px;
    background: #fb8500;
    text-align: center;
    border-radius: 3px;
    margin-bottom: 10px;
}
#usermenu .unread-num {
    display: inline-block;
    font-size: 13px;
    padding: 6px 6px;
    border-radius: 3px;
    min-width: 12px;
    max-width: 48px;
    line-height: 1;
    margin-left: 3px;
    float: right;
    margin-right: 9px;
}
#header .bubble {
    color: white;
    background-color: #fb8500;
}
.post-meta .autor.su_2,
.post-meta-reply .autor.su_2 {
    color: #ffa300;
}
.post-btn.btnmola i,
.post-n.btnmola i {
    background-color: transparent;
    font-size: 18px;
    color: white;
    width:18px;
    height:18px;
    padding-top: 3px;
}
.post-btn.btnmola span,
.post-n.btnmola span {
    font-size: 16px;
    margin-left: 5px;
    color: #ffffff;
}
.col-av .letter,
.col-av img {
    border-radius: 3px;
}
.btnmola {
    background-color: #5e666e;
    color: #b9c8ce;
    border-radius: 3px;
}
.btnmola:hover {
    background-color: #8e9296;
    color: #666666;
    border-radius: 3px;
}

::-webkit-scrollbar
{
  width: 12px;  /* for vertical scrollbars */
  height: 12px; /* for horizontal scrollbars */
}

::-webkit-scrollbar-track
{
  background: rgba(0, 0, 0, 0.1);
}

::-webkit-scrollbar-thumb
{
  background: rgba(0, 0, 0, 0.5);
}
#forum-select{
    width:600px;
    max-height:700px;
}
.event-desc a {
    color: #bfc6cc
}
#content {
    background-position-y: 0;
    background-attachment: fixed;
}
/* i really want this to be global */
#main table.mv td{
    padding:14px 0px
}
.flyout{
    width:470px;
}
.flyout .nano {
    position: relative;
    height: 600px;
    width: 100%;
}
.flyout .content {
    max-height: 600px;
    right:0 !important
}
