style.css:
@-moz-document domain("terra.snellman.net") {
body {
    background-color: #444 !important;
}
div#header,
div#action_required,
#factions > .faction-board,
#factions > .pool {
    background-color: #666;
}
#data_entry_tabs button {
    display: inline-block;
    position: relative;
    width: 120px;
    height: 32px;
    line-height: 32px;
    border-radius: 2px;
    font-size: 0.9em;
    background-color: #999;
    color: #efefef;
}
}
