style.css:
/* 
 * Name:    Twitch Dark Top Bar
 * Title:   Make Twitch dark again with this simple Stylish script
 * Author:  Wilywolf
 * Version: 1.0
 *
 */


@-moz-document domain("www.twitch.tv") {
.top-nav__menu {
    background-color: #1e1e1e;
}
.top-nav__drawer-anchor {
    background: #282828;
}
.top-nav__logo {
    background-color: #141414;
}
.prime-crown__new-offer-count {
    border: 2px solid #323232;
}
.top-nav .notification__number-unread {
    border: .2rem solid #323232;
    border-radius: .4rem;
}
.button {
    background: #1e1e1e;
}
}
