style.css:
@-moz-document domain("www.chickensmoothie.com") {
#page-header {
	background: url(http://i.imgur.com/KlJlA97.png) !important;
	margin: auto;
	background-repeat: no-repeat;
	max-width: 1024px;
	border-radius: 6px;
}
#body_inner {
	background: url(http://i.imgur.com/a09xREV.png);
	background-attachment: fixed;
}
div.body_l,
div.body_r {
	background: url(http://i.imgur.com/Vd0vyXd.png);
}
.headerbar {
	background: url(http://i.imgur.com/Vd0vyXd.png);
}
.bg4 {
	border-top-left-radius: 12px 20px;
	border-top-right-radius: 14px 22px;
	border-bottom-right-radius: 16px 24px;
	border-bottom-left-radius: 18px 26px;
}
}
