.spoiler-input__input {
  border-radius: 4px;
  background-color: #2c2e35;
}


.autosuggest-textarea__textarea {
  height: 200px;
  background: #2c2e35;
  border-radius: 4px 4px 0 0;
  padding-bottom: 0;
  padding-right: 32px;
  resize: none;
}
@media screen and (max-width: 600px) {
  
  .autosuggest-textarea__textarea {
    height: 100px !important;
    resize: vertical;
  }
}


.autosuggest-textarea__suggestions {
  position: absolute;
  top: 100%;
  width: 100%;
  z-index: 99;
  box-shadow: 0 0 15px rgba(0, 0, 0, 0.4);
  background: #dce1e5;
  color: #2c2e35;
  font-size: 14px;
}


.autosuggest-textarea__suggestions__item {
  padding: 10px;
  cursor: pointer;
}

.autosuggest-textarea__suggestions__item:hover {
  background: #bfc8cf;
}

.autosuggest-textarea__suggestions__item.selected {
  background: #6d99ba;
  color: #ffffff;
}


.autosuggest-account {
  overflow: hidden;
}


.autosuggest-account-icon {
  float: left;
  margin-right: 5px;
}


.autosuggest-status {
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}

.autosuggest-status strong {
  font-weight: 500;
}


.character-counter__wrapper {
  line-height: 36px;
  margin-right: 16px;
  padding-top: 10px;
}


.character-counter {
  cursor: default;
  font-size: 16px;
}


.character-counter--over {
  color: #ff5050;
}


.getting-started__wrapper {
  position: relative;
}


.getting-started {
  box-sizing: border-box;
  padding-bottom: 235px;
  -webkit-box-flex: 1;
      -ms-flex: 1 0 auto;
          flex: 1 0 auto;
}

.getting-started p {
  color: #dce1e5;
}

.getting-started a {
  color: #686d7d;
}


.setting-text {
  color: #a5b0c0;
  background: transparent;
  border: none;
  border-bottom: 2px solid #a5b0c0;
  box-sizing: border-box;
  display: block;
  font-family: inherit;
  margin-bottom: 10px;
  padding: 7px 0px;
  width: 100%;
}

.setting-text:focus, .setting-text:active {
  color: #ffffff;
  border-bottom-color: #6d99ba;
}
@media screen and (max-width: 600px) {
  
  .setting-text {
    font-size: 16px;
  }
}


