style.css:
@-moz-document url-prefix("http://www.tvgids.tv/primetime") {
/* Hide like box */
#like-box { display: none; }

/* Hide left and right advertisement columns */
#wrap > .container > .row > .span16,
#wrap > .container > .row > .span30.offset1 { display: none; }

/* Wider content column */
#wrap > .container > .row > .span47.offset1 { width: auto !important; }

/* Place TV network blocks into 3 columns */
#wrap > .container > .row > .span47.offset1 > .section:not(:first-child) {
    width: 300px;
    margin: 50px 20px 0 0;
    float: left;
}

/* First block */
#wrap > .container > .row > .span47.offset1 > .section:nth-child(2),
#wrap > .container > .row > .span47.offset1 > .section:nth-child(3n) {
    clear: left;
    
}

/* Third block */
#wrap > .container > .row > .span47.offset1 > .section:nth-child(3n+5) {
    margin-right: 0;
}


/* Verberg content tijdelijk */
/*#wrap > .container > .row > .span47.offset1 > .section .section-content { display: none; }*/
}
