}
@font-face {
    font-family: 'Inconsolata';
    font-weight: 400;
    font-style: normal;
    src: url('http://fonts.gstatic.com/s/inconsolata/v15/BjAYBlHtW3CJxDcjzrnZCGfQcKutQXcIrRfyR5jdjY8.eot');
    src: url('http://fonts.gstatic.com/s/inconsolata/v15/BjAYBlHtW3CJxDcjzrnZCGfQcKutQXcIrRfyR5jdjY8.eot?#iefix') format('embedded-opentype'), local('Inconsolata Regular'), local('Inconsolata-regular'), url('http://fonts.gstatic.com/s/inconsolata/v15/BjAYBlHtW3CJxDcjzrnZCIgp9Q8gbYrhqGlRav_IXfk.woff2') format('woff2'), url('http://fonts.gstatic.com/s/inconsolata/v15/BjAYBlHtW3CJxDcjzrnZCLrIa-7acMAeDBVuclsi6Gc.woff') format('woff'), url('http://fonts.gstatic.com/s/inconsolata/v15/BjAYBlHtW3CJxDcjzrnZCJ0EAVxt0G0biEntp43Qt6E.ttf') format('truetype'), url('http://fonts.gstatic.com/l/font?kit=BjAYBlHtW3CJxDcjzrnZCHNsigHlcrQGNV8nkUSFQfc&skey=20fa6569a31c71ee&v=v15#Inconsolata') format('svg');
}
html {
    min-height: 100%;
}
td {
    transition: all 75ms ease-in-out;
}
body {
    height: 100%;
    color: #999;
    max-width: 100% !important;
    background: #F0F0F0 !important;
    font-family: 'Raleway', sans-serif !important;
    font-weight: 400 !important;
}
a {
    color: #000 !important;
    text-decoration: none;
    font-family: 'Raleway', sans-serif !important;
    font-weight: 400 !important;
    transition: all 75ms ease-in-out;
}
.mainViewport,
.mainViewportHolder {
    width: 900px !important;
}
a {
    color: black;
}
a:hover {
    color: #a7acd7 !important;
}
a {
    color: #333;
}
.container {
    background: url('http://i.imgur.com/MUIixKM.png') no-repeat !important;
    background-size: 265px !important;
}
.mainUserInfoBox {
    position: relative;
    left: -10px;
    top: -30px;
}
.trPopupDialog {
    width: auto !important;
    padding: 10px !important;
    background: #F0F0F0;
}
.themeHeader a {
    color: black;
    font-family: 'Raleway', sans-serif !important;
    font-weight: 400;
    font-size: 12px;
}
.themeHeader a:hover,
.themeHeader a:focus {
    color: black;
}
.themeHeader .title {
    padding: 25px;
}
.themeHeader .title a {
    text-transform: lowercase;
    font-family: 'Raleway', sans-serif !important;
    font-weight: 100 !important;
    font-size: 52px;
    z-index: 10 !important;
}
.themeHeader .navigation {
    padding: 0;
    height: 0;
    position: absolute;
    left: 100px;
    top: 85px;
    margin: 0;
    z-index: 0 !important;
}
.themeHeader a:hover {
    background: none !important;
    color: #a7acd7 !important;
}
.navHome,
.navUpgrade,
.navMessages,
.navProfile,
.navFriends {
    background: none !important;
}
.themeHeader #tstats {
    padding-top: 10px;
}
.themeHeader #tstats .mainUserInfoBox tbody tr td {
    color: black;
}
.subTitle {
    display: none;
}
.gwt-MenuBar {
    background: #F0F0F0;
}
.gwt-MenuItem-selected {
    background: #3A3C48;
}
.playerInfoPopup {
    background: #292b2c;
    color: black;
    border: 1px red solid !important;
}
.playerInfoPopup a {
    color: black;
}
.playerInfoPopup a:hover,
.playerInfoPopup a:focus {
    color: black;
}
.main,
.themeContent {
    background: 0;
    background-color: #FFF;
    margin: 0 auto;
}
.nonHideableWords {
    letter-spacing: 0.25px;
    word-spacing: 2px;
    font-size: 24px !important;
    font-family: Inconsolata, sans-serif !important;
    font-weight: 400;
    color: black;
}
.friendsTable .headerRow {
    font-size: 14px;
    text-transform: capitalize;
    font-style: normal !important;
}
.friendsTable tr:hover,
.friendsTable tr:focus {
    background-color: #E9E9E9;
}
.friendsTable .headerRow:hover,
.friendsTable .headerRow:focus {
    background-color: #F9F9F9;
}
.gwt-TabBar .gwt-TabBarItem {
    color: #333;
}
.mainUserHeader {
    background: none !important;
}
.footer {
    background: none;
    background-color: #F0F0F0;
    ;
    color: black;
    transition: all 75ms ease-in-out;
}
.footer a:hover {
    color: #a7acd7;
    text-decoration: none;
    transition: all 75ms ease-in-out;
}
.chrome_l,
.chrome_m,
.chrome_r,
.chrome_t,
.chrome_tr,
.chrome_tl,
.chrome_b,
.chrome_bl,
.chrome_br {
    background: 0;
}
a:hover,
a:focus {
    color: #a7acd7;
    transition: all 75ms ease-in-out;
}
.mainViewport,
.main,
.themeContent,
.statsView table {
    background-color: #F0F0F0;
    color: #999;
}
.friendsTable tr:hover,
.friendsTable tr:focus {
    background-color: #F0F0F0;
}
.friendsTable .headerRow:hover,
.friendsTable .headerRow:focus {
    background-color: #F0F0F0;
}
.popupContent {
    background: #F0F0F0;
}
.gwt-TabBar .gwt-TabBarItem {
    color: #888;
}
.gwt-TabBar .gwt-TabBarItem:hover,
.gwt-TabBar .gwt-TabBarItem:focus {
    color: #959bd0;
}
.fullTextStr {
    background: #F0F0F0 !important;
    font-family: Inconsolata, sans-serif !important;
}
.tblOwnStatesNumber {
    color: black!important;
}
.progressBar {
    border: 1px #333 !important;
    border-bottom-style: dashed !important;
}
