style.css:
@-moz-document url-prefix("http://remedy") {
/* Font */

*
{
	font-family: Verdana !important;
	font-size: 9px!important;
	background-color: #000!important;
	color: white!important;
}

textarea, input
{
	background-color: #000;
	color: white;
}

textarea.Editor
{
	background-color: #000;
	color: #fff;
}

::selection
{
	color: #33A1DE;
}

/* Erase top toolbar */

div.ToolBar, div.btnimgdiv
{
	width: 0 !important;
	height: 0 !important;
}

/* Backgrounds colour */

div.Panel fieldset.pnl, div#WIN_0_536876408, div#WIN_0_600008000, div#WIN_0_536876390, div#WIN_0_536876425, div#WIN_0_600001001, div#WIN_0_536876422, div#WIN_0_536876428, div#WIN_0_536876379,div#FormContainer
{
	background-color: rgb(0, 0, 0) !important;
}


td.BaseTableCellOddColor, div.MenuOuter
{
	background-color: #555 !important;
}

fieldset#WIN_0_700000007, fieldset#WIN_0_601001010, div.PageBody.pbChrome
{
	background: -webkit-gradient(linear, 50% 50%, 50% 50%, from(rgb(0, 0, 0)), to(rgb(0, 0, 0))) !important
}

.OuterTab .Tab, .OuterTab .TabLeft, .OuterTab .TabRight, .ScrollingTab .Tab
{
	background: none;
}
/* Click background colour */

tr.SelPrimary td
{
	background-color: #000 !important;
}

tr.Rollover td, div.VNavSelected, tr.SelPrimary td, div.VNavHover0, div.lvl1, td.MenuEntryNameHover, td.MenuEntryNoSubHover, td.MenuEntrySubHover, tr.AutoCompleteSelection td, tr.Rollover td, div.BaseTableHeaderDragL, div.BaseTableHeaderDragR, div.BaseTableHeader, .treetitlerollover, div.VNavHover, div.VNavHover a.btn, fieldset.PageBodyHorizontal
{
	background-color: #707070!important;
}

div.FormContainerBackground, div.FormContainerBackground div, div.BaseTableColHeaders, div.BaseTableHeader, img.btnimgdiv, img.FormContainerImage, tr.DIVPopupTitleBar, div#WIN_0_303610800, div#WIN_0_303610800, div#WIN_0_304316340, div#WIN_3_303877900, div#WIN_0_304260340, div.btnimgdiv div, div#FormContainer
{
	background-color: #000;
	background-image: none !important
}

div.FlyoutContainer.arfid600000200.ardbnNAV-Vertical-Details
{
	background-color: rgb(0, 0, 0);
}

div.VNavSelected.root.arfid536876368.ardbnNAV-CallerDetails, div.root.arfid536876369.ardbnNAV-VehicleDetails, div.root.arfid536876370.ardbnNAV-AddressDetails, div.root.arfid600000103.ardbnNAV-InsuranceDetails, div.root.arfid536876371.ardbnNAV-ProductDetails, div.root.arfid536876372.ardbnNAV-BranchDetails, div.root.arfid536876380.ardbnNAV-AppointmentDetails, div.root.arfid536876394.ardbnNAV-JobNotesDetails, div.root.arfid600000190.ardbnNAV-CSTDetails
{
	background-color: rgb(0, 0, 0) !important;
	border-top: 1px solid rgb(255, 255, 255) !important;
	border-bottom: 1px solid rgb(0, 0, 0) !important;
}

div.BaseTableHeader
{
	background-color: #222 !important;
}

span
{
	background-color: transparent !important;
	background: none !important;
}

/* Scrollbar */

::-webkit-scrollbar
{
	width: 13px;
}

::-webkit-scrollbar-button:vertical:increment
{
	background-color: rgb(140, 140, 140);
}

::-webkit-scrollbar-button:vertical:increment:hover, ::-webkit-scrollbar-thumb:hover
{
	background-color: rgb(82, 82, 82);
}

::-webkit-scrollbar-thumb
{
	background-color: rgb(0, 0, 0);
	border-radius: 0;
}

::-webkit-scrollbar-track
{
	background-color: rgb(158, 158, 158);
}

/* Buttons and borders */

a.btn3d, a.btn3ddown, button.btn3d, button.btn3ddown {
    background: -webkit-gradient(linear,left top,left bottom,from(#aaa),to(#000));
    border: 1px solid;
    border-color: #aaa #666 #aaa #666;
}
}
