/* Skin Created by JZersche */
/* Date Created: 4/18/2017 */
/* Firefox ZDark Theme (Titlebar) */

/* Recommended Addons */
/* Tab Counter can be downloaded from: 
addons.mozilla.org/en-US/firefox/addon/tab-counter */

/* Recommended Styles */
/* https://userstyles.org/styles/140996 [ZDark Context Menus] */
/* https://userstyles.org/styles/140837 [ZDark Theme] */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url("chrome://browser/content/browser.xul") {
  #titlebar-min,
  #titlebar-max,
  #main-window[sizemode="maximized"] #titlebar-max,
  #titlebar-close,
  #minimize-button,
  #restore-button,
  #close-button {
    -moz-appearance: none !important;
    display: -moz-box !important;
    border: none !important;
    padding: 0 !important;
    width: 36px !important;
    height: 32px !important;
  }
  #main-window {
    background-color: var(--second-level-bg-color)!important
  }
  @media (-moz-os-version: windows-xp),
  (-moz-os-version: windows-vista),
  (-moz-os-version: windows-win7),
  (-moz-os-version: windows-win8) {
    #main-window {
      -moz-appearance: none!important
    }
    #main-menubar > menu {
      background-color: rgb(88, 88, 88) !important
    }
    #main-menubar > menu:hover {
      background-color: #09f !important
    }
    #main-menubar > menu:hover:active,
    #main-menubar > menu[open="true"] {
      background-color: #09f !important;
      border-width: 2px!important
    }
    #main-menubar > menu {
      background-color: rgba(32, 32, 32, 0) !important;
    }
    #titlebar-min,
    #minimize-button,
    toolbar[brighttext] #titlebar-min,
    toolbar[brighttext] #minimize-button {
      list-style-image: none!important;
      background: var(--titlebar-min-image) no-repeat center;
      background-position-y: 11px
    }
    #titlebar-max {
      list-style-image: none!important;
      background: url(http://i.imgur.com/yrTG5AP.png) no-repeat center
    }
    #main-window[sizemode="maximized"] #titlebar-close:hover {
      list-style-image: none!important;
      background: url(http://i.imgur.com/CAb1H4Y.png) no-repeat center;
      background-color: red !important;
    }
    #main-window[sizemode="maximized"] #titlebar-max:hover,
    #restore-button:hover {
      list-style-image: none!important;
      background: url(http://i.imgur.com/TQbjwiW.png) no-repeat center
    }
    #main-window[sizemode="maximized"] #titlebar-max {
      list-style-image: none!important;
      background: url(http://i.imgur.com/yrTG5AP.png) no-repeat center
    }
    #titlebar-close,
    #titlebar-close:hover,
    #close-button,
    #close-button:hover {
      list-style-image: none!important;
      background: var(--titlebar-close-image) no-repeat center
    }
    #titlebar-min:hover,
    #titlebar-max:hover,
    #minimize-button:hover {
      background-color: var(--titlebar-min-max-hover-bg-color) !important;
    }
    #titlebar-min:hover:active,
    #titlebar-max:hover:active,
    #minimize-button:hover:active,
    #restore-button:hover:active {
      background-color: var(--titlebar-min-max-active-bg-color)!important
    }
    #titlebar-close:hover,
    #close-button:hover {
      background-color: var(--titlebar-close-hover-bg-color)
    }
    #titlebar-close:hover:active,
    #close-button:hover:active {
      background-color: var(--titlebar-close-active-bg-color)
    }
    .titlebar-button {
      height: 32px!important;
      margin-bottom: -14px!important
    }
    #main-window[sizemode="normal"] #TabsToolbar {
      margin-top: -1px!important;
      margin-left: -1px!important
    }
    #titlebar-buttonbox {
      -moz-appearance: none!important;
      margin-inline-end: 0!important
    }
    .splitmenu-menuitem,
    toolbarbutton.subviewbutton {
      -moz-appearance: none!important;
      color: var(--main-font-color)!important;
      height: 32px!important
    }
    menu {
      -moz-appearance: none!important;
      color: var(--main-font-color) !important;
      height: 32px !important;
    }
    #titlebar-content {height: auto !important; background: #181818 !important;}


    #main-window[sizemode="normal"] > #titlebar {
       -moz-appearance: -moz-window-titlebar;
    }

    #main-window[sizemode="maximized"] > #titlebar {
      -moz-appearance: none; padding-top: 8px !important;padding-bottom: 0px !important; background: #f00 !important;
    }
    
     #titlebar-content {
      -moz-appearance: none; padding-top:0px !important;padding-bottom: 14px !important;background: #181818 !important;
    }

  @media (-moz-windows-classic) {
    #main-window[tabsintitlebar][sizemode="normal"] > #tab-view-deck > #browser-panel > #navigator-toolbox > #toolbar-menubar {
      margin-top: 0px !important; height: 0px !important;border: 1px solid red !important;
     }
