style.css:
@font-face {
font-style: normal;
font-family: "微软雅黑"!important;
font-size: large!important;
src: local("楷体")!important;
}
@font-face {
font-style: bolder;
font-family: "微软雅黑"!important;
font-size: large!important;
src: local("楷体")!important;
}
@font-face {
font-style: normal;
font-family: "微软雅黑"!important;
src: local("微软雅黑")!important;
unicode-range: U+0-FF, U+2E80-9FFF, U+F900-FAFF, U+FE30-FE4F, U+20000-2FA1F!important;
}
@font-face {
font-style: bolder;
font-family: "微软雅黑"!important;
src: local("微软雅黑")!important;
font-size: large!important;
unicode-range: U+0-FF, U+2E80-9FFF, U+F900-FAFF, U+FE30-FE4F, U+20000-2FA1F
}
@font-face {
font-style: normal;
font-family: "微软雅黑"!important;
src: local("Comic Sans MS")!important;
font-size: large!important;
unicode-range: U+0-FF
}
@font-face {
font-style: bolder;
font-family: "微软雅黑"!important;
src: local("Comic Sans MS")!important;
font-size: large;
unicode-range: U+0-FF
}
:not(pre):not(code):not(textarea):not(tt):not(kbd):not(samp):not(var) {
font-family: "微软雅黑"!important;
font-size: 26px!important;
}
pre,
code,
textarea,
tt,
kbd,
samp,
var {
font-family: "Times New Roman"!important;
font-size: large!important;
}
pre *,
code *,
textarea *,
tt *,
kbd *,
samp *,
var * {
font-family: "Times New Roman"!important;
font-size: large!important;
}
