style.css:
@-moz-document domain("userstyles.org") {
body{
    background: black!important;
    max-width: 100% !important;
    width: 100% !important;
}

*{
    color: rgb(200,200,200) !important;
    font-family: Roboto, Arial Nova, Arial, sans-serif !important;
    font-weight: 100 !important;
    font-variant:small-caps !important;
    font-size: 100.2% !important;   
    background:transparent !important;
    border-radius: 0px !important;
    border-color: transparent !important;
    -webkit-transition: ease-in-out 700ms !important;
}

*:hover{
    color: rgb(100,205,225) !important;
    text-shadow: 0px 0px 1.5px rgb(120,225,245) !important;
}

#main-header, #left-sidebar nav, #simple-search>*{
    background: transparent !important;
}
}
