style.css:
@-moz-document domain("dims.karelia.ru") {

body {
    margin: 0 auto;
    max-width: 800px;
    text-align: left;
    line-height: 1.45;
}
pre, code, kbd, tt {
    font: 0.8em "Fira Mono OT", 'lucida console', monospace;
    line-height: 1.5;
}
kbd b {
    letter-spacing: 1.25;
}

/*[[image_align]]*/
/*[[theme]]*/

}

options.value:
kbd {
	padding: 2px 4px;
    color: #d14;
    white-space: nowrap;
    background-color: #f7f7f9;
    border: 1px solid #e1e1e8;
}

pre, pre.box {
    vertical-align: top;
    overflow: hidden;
    display: block;
    padding: 10px 15px;
    box-shadow: 0 1px 3px 0 #eee;
    -webkit-box-shadow: 0 1px 3px 0 #eee;
    -moz-box-shadow: 0 1px 3px 0 #eee;
    border-radius: 3px;
    -webkit-border-radius: 3px;
    -moz-border-radius: 0px;
    margin: 20px 0;
    border: 1px solid #e7e7e7;
    border-bottom: 1px solid #ddd;
    
    color: #333;
    background: #f8f8f8;
}

pre .serif {
	color: green;
}

body {
    background-color: #222;
    color: #fafafa;
}
a, a:visited {
    color: #01ff70;
}
a:hover, a:focus, a:active {
    color: #2ecc40;
}
kbd {
padding: 2px 4px;
    color: #fafafa;
    white-space: nowrap;
    background-color: #333;
    border: 1px solid #444;;
    border-radius: 3px;
}

pre, pre.box {
    background-color: #333;
    border: none;
    padding: .5rem;
    line-height: 1.45;
    overflow-x: auto;
}

pre .serif {
	color: lightgreen;
}

img {
    position: relative;
    left: 50%;
    transform: translateX(-50%);
    
    margin-top: 20px;
    margin-bottom: 20px;
}
