/*
==UserStyle==
@name           N1 Dark Mode
@namespace      N1 Dark Mode
@homepageURL    https://github.com/unigazer/n1-dark-mode
@supportURL     https://github.com/unigazer/n1-dark-mode/issues
@updateURL      https://github.com/unigazer/n1-dark-mode/raw/main/n1.user.css
@license        CC-BY-4.0
@version        1.0.6
@description    Noćni režim za "N1" portal
@author         Vladimir Jovanović
==/UserStyle== 
*/

@-moz-document domain("n1info.com") {

  :root {
    --brand-color: #2c358c;
    --primary: #111;
    --secondary: #1c1c1c;
    --border-color: #2d2d2d;
    --text-color: #bfbfbf;
  }

  @media (prefers-color-scheme: dark) {
    /* General */
    article {
      border: 1px solid var(--border-color) !important;
    }

    .page-template,
    .single,
    .single-post,
    .single .wrapper-singlepost,
    .single-post .wrapper-singlepost,
    .single .in-post-related-news .is-list,
    .single-post .in-post-related-news .is-list {
      background: var(--secondary);
    }

    header {
      background-color: rgba(17, 17, 17, .5) !important;
      backdrop-filter: blur(8px);
    }

    .single .header-title.sticky-header {
      background-image: none;
    }

    .header-bottom .header-nav {
      box-shadow: none;
    }

    .header-bottom {
      background-color: transparent;
    }

    .promoted .promoted-post,
    article {
      background-color: var(--primary) !important;
    }

    blockquote,
    .antrfile {
      background-color: var(--primary);
      background-image: none;
    }

    /* Shoppster logo, chevron */
    #menu-item-8674444 > a:nth-child(1) > i:nth-child(1) > svg:nth-child(1) > g:nth-child(3) > g:nth-child(1) > path:nth-child(1),
    #menu-item-8674444 > a:nth-child(1) > i:nth-child(1) > svg:nth-child(1) > g:nth-child(3) > g:nth-child(1) > path:nth-child(2),
    #menu-item-8674444 > a:nth-child(1) > i:nth-child(1) > svg:nth-child(1) > g:nth-child(3) > g:nth-child(1) > path:nth-child(3),
    #menu-item-8674444 > a:nth-child(1) > i:nth-child(1) > svg:nth-child(1) > g:nth-child(3) > g:nth-child(1) > path:nth-child(4),
    #menu-item-8674444 > a:nth-child(1) > i:nth-child(1) > svg:nth-child(1) > g:nth-child(3) > g:nth-child(1) > path:nth-child(5),
    #menu-item-8674444 > a:nth-child(1) > i:nth-child(1) > svg:nth-child(1) > g:nth-child(3) > g:nth-child(1) > path:nth-child(6),
    #menu-item-8674444 > a:nth-child(1) > i:nth-child(1) > svg:nth-child(1) > g:nth-child(3) > g:nth-child(1) > path:nth-child(7),
    #menu-item-8674444 > a:nth-child(1) > i:nth-child(1) > svg:nth-child(1) > g:nth-child(3) > g:nth-child(1) > path:nth-child(8),
    #menu-item-8674444 > a:nth-child(1) > i:nth-child(1) > svg:nth-child(1) > g:nth-child(3) > g:nth-child(1) > path:nth-child(9),
    .header-title a .Arrow_Right svg,
    .lang-switcher__menu > li.menu-item:first-child > a .sprite-icon svg,
    .n1 .header .menu__desktop-right .menu-item--program > a .sprite-icon svg,
    .n1 .navigation-right .searchbox .searchbox-icon .Search-Active {
      fill: #fff;
    }

    h1, h2, h3, h4, h5, p, span, strong,
    .header-bottom .header-nav .navigation .dropdown.menu.menu--desktop li.menu-item a,
    .lang-switcher__menu > li.menu-item:first-child > a,
    .promoted .promoted-post__title-link,
    article .uc-block-post-grid-header .uc-block-post-grid-title a,
    article .mobile .uc-block-post-grid-date,
    article .uc-block-post-grid-header .uc-block-post-grid-byline .uc-block-post-grid-date,
    article .mobile .total-comments,
    article .uc-block-post-grid-header .uc-block-post-grid-byline .total-comments,
    article .mobile .uc-block-post-grid-cat_name .uc-block-post-grid-cat_name_link,
    article .uc-block-post-grid-header .uc-block-post-grid-byline .uc-block-post-grid-cat_name_link,
    article .uc-block-post-grid-header .uc-block-post-grid-byline a,
    .page-template-footer-pages .footer-pages .entry-title,
    .header-title a,
    .header-title h3,
    .footer-top section h2,
    .single .main-head .entry-title,
    .single-post .main-head .entry-title,
    .single p,
    .single-post p,
    .single p strong,
    .single-post p strong,
    .single-post .entry-content h2,
    .single .in-post-related-news .uc-post-grid-block-header .uc-post-grid-section-title,
    .single-post .in-post-related-news .uc-post-grid-block-header .uc-post-grid-section-title,
    .sidebar .uc-post-grid-block-header .uc-post-grid-section-title,
    .related-components .uc-post-grid-block-header .uc-post-grid-section-title,
    .single .main-content footer span.tags-title,
    .single-post .main-content footer span.tags-title,
    .single .main-head .post-author a,
    .single-post .main-head .post-author a,
    .promoted .promoted-post__cat-link,
    #live-blog-wrapper .uc-liveblog-main-content .uc-liveblog-main-title,
    #live-blog-wrapper .uc-liveblog-content .uc-liveblog-entry p,
    #live-blog-wrapper .uc-liveblog-content .uc-liveblog-title,
    #live-blog-wrapper .uc-liveblog-content .uc-liveblog-entry {
      color: var(--text-color);
    }

    .footer-inner {
      background-color: var(--primary);
    }

    /* Comments */
    .coral-comment {
      background-color: var(--primary);
      border: 1px solid var(--border-color);
    }

    #coral_wrapper .inner-coralusername,
    .coral-createComment {
      background: var(--primary) !important;
      border: 1px solid var(--border-color) !important;
    }

    #coral_wrapper .inner-coralusername #coraltalk-username[type="text"],
    .coral-comment .coral-comment-content,
    .coral-comment .coral-comment-username span,
    #coral_wrapper .inner-coralusername .coral-title,
    .coral-tabBarComments .coral-tabBarComments-allComments.Tab-active-XrGkw {
      color: var(--text-color) !important;
    }

    .content.placeholder.toolbar > div,
    input[type="text"],
    input[type="text"]:focus {
      background: var(--secondary);
    }

    .CallOut-colorRegular-1TwSR {
      background-color: var(--secondary) !important;
    }

    .content.placeholder.toolbar > div > div {
      border: 1px solid var(--border-color) !important;
      color: var(--text-color) !important;
    }

    .coral-createComment::before,
    #coral_wrapper::before {
      background: none !important;
    }

    /* Heading */
    .footer-top section h2::before {
      background-color: var(--brand-color);
    }

    /* Nav Articles */
    .layout-4,
    .inner-btn .inner-load-more,
    .inner-btn .inner-load-more .btn {
      background-color: var(--primary);
    }

    .lang-switcher__menu > li.menu-item a,
    .inner-btn .inner-load-more .btn,
    .header .searchbox.searchbox-open .searchbox-input {
      color: var(--text-color);
    }

    .n1 .header .menu__desktop-right .menu-item--program a {
      color: var(--text-color) !important;
      cursor: pointer;
    }

    .header .searchbox.searchbox-open {
      background-color: var(--secondary);
    }

    /* Dropdown */
    .lang-switcher__menu .sub-menu.is-dropdown-submenu,
    .header-bottom .header-nav .navigation .dropdown.menu.menu--desktop li.menu-item ul.is-dropdown-submenu,
    .n1 .header .menu__desktop-right .sub-menu.is-dropdown-submenu {
      background-color: var(--secondary);
    }

    .lang-switcher__menu .sub-menu.is-dropdown-submenu .menu-item a,
    .header-bottom .header-nav .navigation .dropdown.menu.menu--desktop li.menu-item ul.is-dropdown-submenu li.is-dropdown-submenu-item a {
      color: var(--text-color);
    }

    /* Social */
    .heateor_sss_sharing_ul .heateorSssSharingRound i svg {
      fill: var(--text-color);
    }

    /* Cookies */
    #CybotCookiebotDialog {
      background-color: rgba(17, 17, 17, .5) !important;
      backdrop-filter: blur(8px);
    }
    #CybotCookiebotDialog #CybotCookiebotDialogBody #CybotCookiebotDialogBodyContent #CybotCookiebotDialogBodyContentTitle,
    #CybotCookiebotDialog #CybotCookiebotDialogBody #CybotCookiebotDialogBodyContent #CybotCookiebotDialogBodyContentText,
    #CybotCookiebotDialog #CybotCookiebotDialogBody #CybotCookiebotDialogBodyButtons #CybotCookiebotDialogBodyButtonDetails {
      color: var(--text-color) !important;
    }

    /* Search Page */
    body.search {
      background: var(--secondary);
    }

    .search-content .searchbox [type="text"] {
      color: var(--text-color);
    }

    .search-content .searchbox .Search {
      fill: #fff;
    }

    /* Live (Timeline) */
    .uc-liveblog-content {
      background-color: var(--primary);
      box-shadow: 0px 1px 12px var(--primary) !important;
    }
  }

  .top-zone,
  .banner-promotion {
    display: none;
  }
}
