style.css:
@-moz-document domain("sabishiidesu.com") {
/*
* Author: Ew0345
* Description: Changes SabishiiDesu's site to a dark theme instead of the bright white.
* Version: 3.0
* Notes: Updated for the current version of the site... Finally... Sorry for taking so long T^T
*/
}

@-moz-document domain("www.sabishiidesu.com"), domain("sabishiidesu.com") {
body,
#main,
#content {
    background-color: #232323 !important;
    color: #eee !important;
}
#sb1,
#sb2,
#site-header-container {
    background-color: #171717 !important;
}
.widget,
.widget-title {
    background-color: #171717 !important;
    color: #ccc !important;
}
.widget a:link,
.widget a:visited,
.entry-meta,
.inner.footer.meta,
.entry-meta a {
    color: #ccc !important;
}
.entry-meta a {
    text-decoration: underline !important;
}
.widget a:hover {
    text-decoration: underline !important;
}
.input input,
input[type="email"] {
    background-color: #303030 !important;
    color: #aaa !important;
}
.site-title,
.site-title a {
    color: lightslategray !important;
}
.site-description {
    color: slategray !important;
}
span[itemprop="name"] {
    color: #eee !important;
}
a:hover span[itemprop="name"] {
    text-decoration: underline !important;
}
#above-footer-ad-container,
#copyright-container {
    background-color: #171717 !important;
}
article footer,
article footer a time {
    background-color: #232323 !important;
    color: #ccc !important;
    border-color: darkgray !important;
}
.entry-title,
.entry-title a {
    color: #eee !important;
}
}
