style.css:
#TabsToolbar ~ #nav-bar {
 box-shadow: none !important;
}
#main-window #TabsToolbar ~ toolbar#nav-bar {
 margin-top: -1px !important;
 border-top: 1px solid transparent !important;  /* Firefox 32+ */
}
#nav-bar {
 background: none !important;
}
#main-window:not([sizemode='fullscreen']) #navigator-toolbox {
  margin-bottom: -1px!important;
}
.tabbrowser-tab:not([pinned])[fadein] {
  width: 200px!important;
  min-width: 40px!important;
}
#TabsToolbar{
   margin:0 -4px 0 -4px !important;
  -moz-box-ordinal-group:2 !important;
}
#nav-bar {
  padding-right: 110px;
}
#titlebar-placeholder-on-TabsToolbar-for-captions-buttons{
  max-width: 1px!important;
}
