style.css:
@-moz-document domain("www.citationmachine.net") {
/*Inline ads*/
.col-sm-5,
.col-md-2,
.col-sm-12,

.search-bar .block .fill-mode,
#citation-copied:not(h4) div,
#tips-guide,
#how-cm-works,
div #footer,
.timer-alert:not(.countdown-timer) a,
.timer-alert:not(.countdown-timer) u,
#accounts,
#bibliography-signup,
[data-type="premium-links"]:not(#copy-full-bib),
.form-ibv-right,
#unit-responsive,
#citation-splash,
.step,
[herf*="studybreakmedia"],
[class*="grammarly"],
iframe,
div[style*="fixed"][style*="100vw"][style*="100vh"][style*="z-index"],
div[style*="fixed"][style*="100%"][style*="100%"][style*="z-index"],
div[style*="fixed"][style*="100vw"][style*="100%"][style*="z-index"],
div[style*="fixed"][style*="100%"][style*="100vh"][style*="z-index"],
[data-reactroot] /*May cause unwanted changes,*/
{
    display: none!important;
    width: 0!important;
    height: 0!important;
    max-width: 0!important;
    max-height: 0!important;
    min-width: 0!important;
    min-height: 0!important;
    filter: opacity(0)invert(50%);
    visibility: hidden!important;
    z-index: 0!important;
    color:rgba(0,0,0,0)!important;
    background-color:rgba(0,0,0,0)!important;
    font-size:0!important;
    position:fixed;
    left:-10000px!important;
    top:-10000px!important;
    transform:scale(0) skew(90deg,90deg)!important;
    -webkit-transform:scale(0) skew(90deg,90deg)!important;
    -ms-transform:scale(0) skew(90deg,90deg)!important;
    -webkit-perspective:0;
    perspective:0;
    border:none 0 rgba(0,0,0,0);
    opacity:0
}
body{
    background-color:#696969
}
.nav-item a{
	color:red!important
}
#content,
#citation-guide,
#choose-style,
.block,
#enter-citation,
#enter-citation #citations .section_source,
#enter-citation #citations .section_contributors,
#enter-citation #citations .section_publication,
#enter-citation #citations .section_annotation,
#enter-citation #citations .section_advedit,
#enter-citation #citations .section_doi{
    background-color: #b8b8b8!important
}
.guide {
    background-color: #e8e8e8!important
}
@media screen and (min-width:777px) and (max-width:799px){
    .container{
        padding-left:40px!important;
		padding-right:10px!important
    }
}
@media screen and (min-width:800px) and (max-width:991px){
    .container{
        width:100vw
    }
}
@media screen and (min-width:992px) and (max-width:1199px){
    .container{
        width:170vw;
		padding-right:20px!important
    }
}
@media screen and (min-width:1200px) and (max-width:1399px){
    .container{
        width:177vw;
		padding-right:110px!important
    }
}
@media screen and (min-width:1400px){
    .container{
        width:177vw;
		padding-right:140px!important
    }
}
.container{
    margin:0;
    padding:0 5vw
}
body, html, :root{
	overflow-x: hidden!important
}
.bibliography-item-copy-text,
#parenthetical_citation{
    font-family:"Times New Roman";
    font-size:12px;
    font-weight:normal;
    cursor:text!important;
    background:none!important
}
#citation-copied{
    padding:3px!important;
    margin:0!important;
    text-align:center
}
}
