style.css:
@-moz-document domain("www.pinterest.com") {
body {
    background: #fff;
}
.absoluteWrapper {
    background: #fbfbfb;
}
.pinWrapper {
    background: #fff;
    border: 1px solid #ddd;
    box-shadow: 0px 6px 10px rgba(0, 0, 0, .1);
    border-radius: 0 !important;
}
.pinWrapper:hover {
    box-shadow: 0px 10px 20px rgba(0, 0, 0, .15);
}
.pinWrapper::before {
    border-radius: 0 !important;
}
.Module.UserProfilePage.fadeIn {
    background: #fff;
}
.ReactBoardHeader {
    background: #fff;
}
}
