style.css:
/**
   Add your instance to the domain list
**/

@-moz-document domain("mastodon.social"),
domain("mastodon.xyz"),
domain("mamot.fr") {
    .column {
        min-width: 300px;
        width: 35%;
    }
    .column:nth-last-child(2) {
        order: 1;
    }
    .drawer,
    .column:nth-last-child(2) {
        min-width: 250px;
        width: 15%;
    }
}
