style.css:
@-moz-document domain("vk.com") {
  /* Тени */

  .wall_module .post {
   transition: all .24s ease-in-out;
  }

  .wall_module .post:hover {
    box-shadow: 0 0 25px rgba(0, 0, 0, .30); 
    border-radius: 3px;
    z-index: 2;
  }



  .ui_search_custom input.ui_search_field, .page_block, input.ui_search_field, .profile_more_info_link, .left_fixer, .im-chat-input, .im-page_classic.im-page.im-page_history-show .im-page--history {
   transition: all .24s ease-in-out;
  }
  .ui_search_custom input.ui_search_field:hover, .page_block:hover, input.ui_search_field:hover, .profile_more_info_link:hover,  .im-chat-input:hover, .im-page_classic.im-page.im-page_history-show .im-page--history:hover {
    box-shadow: 0 0 25px rgba(0, 0, 0, .30); 
    border-radius: 3px;
    z-index: 2;
  }



/* Фон меню */
#side_bar ol li .left_row {
    background: #ffffff;
}  

.left_count_wrap {
    color: #ffffff;
}  

  /* Цвет счётчиков в меню */

  .left_count_wrap, #side_bar ol li .left_row:hover .left_count_wrap, .font_medium .left_count_wrap, .font_medium #side_bar ol li .left_row:hover .left_count_wrap {
    background-color: #507299;
 }
}
