style.css:
@-moz-document url(http://youhack.ru) {.navTabs {
    background: #FF4446;
    border: none;
}
#logo a span {
    color: #fff;
}
.visitorTabs .alerts.unread .fa,
.visitorTabs .inbox.unread .fa {
    color: #4D6AFF;
}
.navTabs .navTab.selected .navLink {
    background-color: #fff;
    color: #000;
}
.navTabs .navTab .navLink:hover {
    background-color: #fff;
    color: #000;
}
.navTabs .navTab.PopupClosed .navLink:hover {
    color: #000;
}
.topHelper {
    background-color: #fafafa;
    box-shadow: 0 1px 10px rgba(0, 0, 0, 0.1);
}
#content {
    background-color: #dcdcdc;
}
.navTabs .navTab.selected .tabLinks {
    background-color: #FF4446;
}
.navTabs .navTab.selected .tabLinks
a {
	color: #fff;
}

.navTabs .navTab.selected .tabLinks
a:hover{
	color: #dcdcdc;
}}
