style.css:
@-moz-document domain("chat.vanila.io") {
/* Page load */
.full-page, .page-loading {
    background: #232830 !important;
}
#login-card:hover {
    box-shadow: 0px 0px 50px #b75d5d !important;
}
#login-card .input-line label,
#login-card .register,
#login-card .forgot-password {
    font-size: 16px !important; 
    font-weight: bold;
    color: black !important;
}
/* Background color */
.messages-container .wrapper,
.flex-tab .content {
    background: linear-gradient(120deg, #fafafa, #fff) !important;
}
/* Channel name focus */

.fixed-title {
    box-shadow: 0px 0px 20px #b75d5d !important;
    border: none !important;
}
/* Chat window */

body .color-primary-font-color a {
    color: #0D47A1 !important;
    background: #CCFF90 !important;
}
body .color-primary-font-color blockquote div div a {
    background: transparent !important
}
/* Chat window message on mouse hover */

.message,
.background-transparent-dark-hover {
    transition: all 200ms ease-in-out;
}
.background-transparent-dark-hover:hover {
    transition: all 200ms ease-in-out;
}
/* Left sidebar panel chat rooms */

.flex-nav .content h4,
.account-box .info h4,
.flex-nav header h4,
.flex-nav {
    color: #fff !important;
}
.name,
.side-nav i.status-offline,
.input-line.search i,
.side-nav h3.add-room i,
.wrapper a,
.wrapper h3,
.wrapper p {
    color: #fff !important;
}

i.status-online {
    color: lime !important;
}

i.status-away {
    color: yellow !important;
}

i.status-busy {
    color: red !important;
}
/* Rooms link */

.message-form .message-buttons {
    color: #263238 !important;
    background: #fff !important;
}
.message-form .message-buttons:hover {
    background: #E0E0E0 !important;
}
/* @mention color */

.user .user-card-message .color-primary-font-color,
.attachment-description p {
    color: #000 !important;
}
/* Left sidebar background color */

.primary-background-color,
.side-nav .rooms-list,
.flex-nav .content {
    background: linear-gradient(/*[[degrees-angle]]*/, /*[[sidebar-color-1]]*/, /*[[sidebar-color-2]]*/) !important;
}
/* Messaging input form */

.messages-container .message-form>.message-input:focus {
    box-shadow: 0px 0px 10px #b75d5d !important;
}
/* Left sidebar dropdown menu text */
.side-nav .options button {
  background-color: /*[[sidebar-color-2]]*/ !important;
}  
.c-select option {
    color: #000 !important;
}
/* Right sidebar */

.flex-tab .content,
.flex-tab .user-view,
.flex-tab .list-view {
    background: #FAFAFA !important;
    box-shadow: 0px 0px 50px #BDBDBD !important;
    box-shadow: 0px 4px 4px -2px #b75d5d !important;
}

.flex-tab-container .flex-tab {
    width: 220px !important;
}

.list-view {
    padding: 0 !important;
}

.flex-tab-container {
    height: 450px !important;
}

.user-view .info {
    margin-top: -70% !important;
}
/* Title */
.title {
    padding: 10px 30px;
}
/* List of users */
.lines .user-image, .flex-tab .push-notifications ul li {
    padding-left: 30px;
    padding-right: 30px;
}
/* Avatar */

.avatar .avatar-image {
    height: 100% !important;
    border-radius: 100px;
    box-shadow: 0px 4px 4px -2px #bfbfbf;
    border: 1px solid #9E9E9E;
}
.user-view .thumb {
    padding-left: 20px !important;
    padding-right: 20px !important;
    padding-bottom: 150px !important; 
}
/* Buttons on right sidebar */
  
.button.primary {
    background-color: #0084ff !important;
    transition: all 300ms ease-in-out;
}
.button.primary:hover {
    background-color: #0074db !important;
    transition: all 300ms ease-in-out;
    box-shadow: 0px 4px 4px -2px #bfbfbf;
}
/* Search and Message placeholder */

::-webkit-input-placeholder {
    color: #ffffff !important;
}
::-moz-placeholder {
    color: #ffffff !important;
}
/* Animations */
  
.messages {
    animation-duration: 300ms;
    animation-fill-mode: both;
    animation-name: fadeIn;
}
@keyframes fadeIn {
  from {
    opacity: 0;
  }

  to {
    opacity: 1;
  }
}
}
