options.value:
@-moz-document regexp('(http|https):\/\/(gelbooru|youhate)\.(com|us)/+index\.php\\?page=post.*') {
  body {
    background-color: #222!important;
  }
  a:hover{
    opacity: 0.8;
  }
  span a[id]:hover{
    opacity:1!important;
  }
  .thumbnail-preview {
    padding: 0px!important;
    border-color: #000!important;
    background-color: rgba(255, 255, 255, 0)!important;
  }
  .hidden-xs {
    border-width: 0px!important;
    padding: 0px!important;
  }
  #searchTags h4{
    color: #DDD!important;
  }
  .webm {
    border-color: #1a5ccf!important;
  }
  .pagination a {
    color: #fff!important;
    border-color: #555!important;
    background-color: #333;
    transition: background-color 0.5s ease;
  }
  .pagination b{
    color: #fff!important;
    border-color: #555!important;

  }
  .pagination a:hover {
    background-color: #222!important;
    transition: background-color 0s ease;
  }
  img {
    color: #333!important;
  }
  .navbar-default .navbar-nav > li > a{
      color: #fff!important;
  }
  #tag-list {
    background-color: #333!important;
    border-right-color: #000!important;
  }
  #tag-list div{
    color: #DDD!important;
  }
  #tag-list div a{
    color: #4fc3f7;
  }
  .tag-type-general span, .tag-type-artist span, .tag-type-character span, .tag-type-copyright span{
    color: #ddd!important;
  }
  .tag-type-general a {
    color: #4fc3f7!important;
  }
  .tag-type-artist a {
    color: #e53935!important;
  }
  .tag-type-character a {
    color: #4caf50!important;
  }
  .tag-type-copyright a {
    color: #ba68c8!important;
  }
  .tag-type-metadata a {
    color: #ffa239!important;
  }
  input {
    color: #DDD!important;
    background-color: #222!important;
    transition: background-color 0.3s ease, border-color 0.5s ease;
  }
  input:hover{
    background-color: #111!important;
    transition: background-color 0s ease, border-color 0s ease;
  }
  #tags-search {
    border-color: #000!important;
  }
  #tags-search:focus{
    border-color: #777!important;
    transition: background-color 0.1s ease, border-color 0s ease;
  }
  .navbar {
    background: #333!important;
  }
  .navbar .active a{
    color: #FFF!important;
    background-color: #222!important;
  }
  #right-col div:not(.note-body){
    color: #DDD!important;
  }
  #right-col a{
    color: #4fc3f7!important;
  }
  .note-box-inner-border {
    border: 1px solid #66d!important;
    background: #F4F4FF!important;
  }
  .note-body {
    color: #DDD!important;
    background: #333!important;
    background-color: #333!important;
  }
  .alert-info {
    background-color: #333!important;
    color: #DDD!important;
  }
  .alert-info a {
    color: #EEE!important;
  }
  .contain-push{
    color: #DDD!important;
  }
  div div[style][id]{
    color: #DDD!important;
    background-color: #333!important;
    border-color: #000!important;
  }
  div div[style][id] a{
    color: #4fc3f7!important;
  }
  .ui-menu-item{
    color: #DDD!important;
    background-color: #333!important;
  }
  .commentBox{
    color: #DDD!important;
    background-color: #333!important;
    border-color: #000!important;
  }
}

