style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mysku.ru") {

body {
font: 13px/19px Arial,Helvetica,sans-serif;
color: #babdb6;
background: #282b2f;
min-height: 100%;
}


#content {
background: #282b2f;
padding-top: 11px;
padding-bottom: 58px;
padding-right: 15px;
margin-right: 300px;
}

a {
color: #6DA3BD;
}

.profile-blog .title {
    color: #e09704;
}

.most-discussed-topics-wrap .block-title {
    color: #e09704;
}

.most-discussed-topics-wrap .tab-item {
    border-top: 1px solid #666;
}

.overview-wrap .overview-block .tab-item .string .rate {
    background-color: #222;
    border-bottom: 1px solid #CCC;
}

.overview-wrap .overview-block .tab-item .string .inner:hover {
    cursor: pointer;
    background-color: #222;
}
.overview-wrap .overview-block .tab-item .string .inner {
    border: 1px solid #666;
        border-top-color: rgb(102, 102, 102);
        border-top-style: solid;
        border-top-width: 1px;
        border-left-color: rgb(102, 102, 102);
        border-left-style: solid;
        border-left-width: 1px;
    border-left: 0;
    padding: 7px;
        padding-right: 7px;
    padding-right: 19px;
    width: 100%;
    border-top: 0;
}

.overview-wrap .overview-block .tab-item .string .rate {
    background-color: #222;
    border-bottom: 1px solid #666;
}

.overview-wrap .overview-block .tab-item .string .inner .title a {
    margin-bottom: 1px;
    color: #b38840;
    text-decoration: none;
}
    
ul.category-list a[href$='promo-reviews']{
    display: block !important;
    background: none !important;
    background-color: #e75f5f !important;
    color: #222 !important;
    border-radius: 4px;
    border: solid 1px #7b4444;
}
    
ul.category-list a[href$='https://mysku.ru/blog/diy']{
    display: block !important;
    background: none !important;
    background-color: #4f4f97 !important;
    color: #CCC !important;
    border-radius: 4px;
    border: solid 1px #8b84Cb;
}


ul.category-list a[href$='https://mysku.ru/catalog/115-umnyy-dom']{
    display: block !important;
    background: none !important;
    background-color: #c2a02d !important;
    border-radius: 4px;
    border: solid 1px #f2c67c;
}
    
.topic .topic-title a, .topic .topic-title h1, .topic-title span {
text-decoration: none;
border-bottom: 2px solid #6DA3BD;
color: #6DA3BD;
}

.topic .topic-title a:hover {
border: 0px none;
color: #6DA3BD;
}

.sku-info .sku-info-line li a {
color: #6DA3BD;
text-decoration: none;
border-bottom: 1px solid #6DA3BD;
}



.topic .topic-footer li.date {
border: 0px none;
padding-left: 0px;
color: #babdb6;
}
  

ul.category-list a.list-item-link {
display: inline-block;
background: #babdb6;
border-radius: 3px;
padding: 0px 8px 1px 4px;
margin: 0px;
height: 14px;
line-height: 14px;
color: #494949;
font-size: 10px;
text-transform: uppercase;
font-family: "PT Sans Caption";
text-decoration: none;
font-weight: bold;
}

.read_all a {
background: #babdb6 none repeat scroll 0% 0%;
color: #000;
display: inline-block;
border-radius: 4px;
border: 1px solid #F6F6F6;
padding: 0px 7px 2px;
text-decoration: none;
font-weight: normal !important;
}

.search {
width: 250px;
height: 30px;
margin-bottom: 1em;
position: absolute;
left: 5px;
top:7px;
visibility: visible;

}

.search .button {
    box-shadow: none;
}


#sidebar {

float: right;
margin-top: 0px;
margin-left: 0px;
visibility: hidden;
}

#content-container {
float: left;
width: 100%;
}

#content {
padding-top: 11px;
padding-bottom: 58px;
padding-right: 15px;
padding-left: 15px;
margin-right: 15px;
}
    
.container {
    margin: 30px 15px;
    min-width: 905px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    -ms-box-sizing: border-box;
    box-sizing: border-box;
}
    
.profile-user .name div.nickname {
    font-size: 24px;
    margin-top: 2px;
    margin-bottom: 10px;
    color: #d7cdcd;
    float: left;
}

   
    
.menu li a {
    color: #FFF;
    font-weight: bold;
    border-bottom: none;
}
    
.menu li.active > a {
    color: #ffd800;
}
    
    
.sub-menu {
    margin-top: 10px;
    }


    
.sub-menu li.active a {
    color: #ffd800;
  
}
    
    
.h_main {
position: relative;
z-index: 2;
overflow: hidden;
visibility: hidden;
}

#header {
height: 40px;
clear: both;
overflow: hidden;
padding: 0px;
min-width: 890px;
}

#header .profile {
float: right;
padding-top: 5px;
padding-right: 14px;
padding-left: 0px;
}


.h_side {
position: relative;
float: right;
width: 317px;
margin-right: -30px;
padding-left: 14px;
z-index: 3;
}

#nav {
height: 72px;
position: absolute;
left: 220px;
top: -10px;

}

.header_violet_line {
float: left;
width: 100%;
padding: 0px;
background: #272c2d;
border-bottom: 1px solid #B9B9C1;
border-top: 1px solid #42427E;
box-sizing: border-box;
}


.comment .content .main_side {
margin-right: 28px;
padding: 10px 35px 9px 33px;
position: relative;
border: 1px solid #535353;
border-radius: 5px;
background: #373b3e;
}
.main_side.new {
border: 1px solid #6DA3BD;
background: #272c2d;
}

.comment .content .text {
margin-bottom: 4px;
overflow: hidden;
font-size: 14px;
color: #babdb6;
}

.comment .info p a.author, .comment .info .user_name a {
font-weight: bold;
color: #6DA3BD;
text-decoration: none;
}

.purchase_button span.number {
position: relative;
float: left;
color: #333;
padding: 2px 7px;
border-left: 1px solid #DDD;
background: #6CA54E;
}

.purchase_button span.arrow:hover, .purchase_button span.text:hover {
background: #AEE58E;
}

.purchase_button span.text {
background-color: #6CA54E;
cursor: pointer;
float: left;
color: #333;
padding: 2px 8px;
}

.spoiler {
display: block;
border: 1px solid #555;
padding: 0px;
background: #272c2d;
border-radius: 5px;
margin-top: 20px;
margin-bottom: 20px;
overflow: hidden;
position: relative;
}

.spoiler .spoiler-title {
color: #6DA3BD;
cursor: pointer;
margin: 10px 11px;
}

.spoiler .spoiler-body {
margin: 0px;
padding: 13px 11px 10px;
background: #272c2d none repeat scroll 0% 0%;
overflow: hidden;
display: none;
}


.topic .topic-footer {
font-family: Verdana,sans-serif;
border-top: 1px solid #666;
padding: 11px 0px;
overflow: hidden;
}

.comments .header .title {
margin-bottom: 0px;
color: #6DA3BD;
display: inline-block;
font-size: 18px;
}
.white-back {
background: #282b2f none repeat scroll 0% 0%;
margin: 0px 20px 30px;
padding-bottom: 15px;
padding-left: 20px;
}

.main_side.self {
background: #35424b none repeat scroll 0% 0% !important;
}

.user-topic-starter {
background: #191D1E;
padding: 2px;

}

.main_side.new {
background: #191D1E none repeat scroll 0% 0% !important;
}
  
.comments .comment .comment {
    padding-left: 32px;
}
    
.paginator li.active {
    background: #6DA3BD;
    color: black;
}

.paginator li .a-link {
display: inline-block;
font-size: 14px;
color: #6DA3BD;
text-decoration: none;
}

.topic .topic-footer li.author a {
font-weight: bold;
color: #6DA3BD;
}
  
.topic{
  background: #373b3e;
  padding:15px;
  padding-bottom: 0px;
  border-radius: 4px;
  border: 1px solid #484b4e;
  }

.topic .content img, .topic-content img {
    max-width: 100%;
    display: block;
    margin: 11px 19px 9px 1px;
    border: solid 4px #484b4e;
    border-radius: 2px;
    -webkit-box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.106);
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.106);
}  
  
  .comment .content blockquote {
    padding: 5px 10px;
    border-left: solid 3px #5777c7;
    color: #AAA;
    margin: 12px 0;
    clear: both;
    background-color: #282b2f;
}
  
.similar-posts .header .title {
    margin-bottom: 0;
    color: #6DA3BD;
    display: inline-block;
    *display: inline;
    zoom: 1;
    font-size: 18px;
}
  
#footer {
    border-top: 1px solid #666;
    background: none;
    padding: 30px 0;
    margin: 0 30px;
    color: #494949;
    overflow: hidden;
}
  
.profile-blog span.rss a {
    display: inline-block;
    *display: inline;
    zoom: 1;
    color: #666;
    background: url(http://assets.mysku-st.ru/templates/skin/mysku.v3/images/rss_icon.png) 0 50% no-repeat;
    padding-left: 16px;
}
  
 .profile-blog span.subscription {
    float: left;
    color: #666;
    margin-bottom: 20px;
    border-left: solid 1px #666;
    padding-left: 12px;
    margin-right: 12px;
}
  .write a.button.small {
    float: left;
    height: 36px;
    margin: 0;
    padding: 0;
    background: #48A149;
    border: 1px solid #78E172;
    border-radius: 4px;

}
  
.write a.button.small span {
    margin-left: -4px;
    height: 36px;
    background: none;
}
.write a.button.small em{
  background: none;  
 }


  
.ilike_button span.number {
    color:#222;
}  
  
.ilike_button span {
    float: left;
    background: #BABDB6;
    color: #222;
}
  .ilike_button span.number span.counter {
    background: none;
}
  
.topic .topic-footer li {
    border-left: solid 1px #666;

}
  
.topic .topic-footer li.comments-total a span.red {
    color: #DB5A49;
}
  
.topic .topic-footer li.comments-total a span.green {
    color: #3e990e;
}
  .paginator li {
    display: inline-block;
    *display: inline;
    zoom: 1;
    padding: 3px 8px;
    margin: -3px 3px;
    background: #3b3b3b;
    border-radius: 12px;
}
  
.topic-coupon {
    border: solid 1px #d5d2c6;
    padding: 14px 19px 8px;
    overflow: visible;
    position: relative;
    margin-bottom: 1em;
}
  
.topic-coupon {
    border: solid 1px #20536b;
    padding: 14px 19px 8px;
    overflow: visible;
    position: relative;
    margin-bottom: 1em;
    border-radius: 5px;
    background: #20536B;
}  
.topic-coupon s.corner.top-left {
    background:none;
    top: -1px;
    left: -1px;
}  
  .topic-coupon s.corner.top-right {
    background: none;
    top: -1px;
    right: -1px;
}
  
  .topic-coupon s.corner.bottom-left {
    background: none;
    bottom: -1px;
    right: -1px;
}
  
  .topic-coupon s.corner.bottom-right {
    background: none;
    bottom: -1px;
    left: -1px;
}
  
  .topic-coupon .coupon-body-margins {
    display: inline-block;
    *display: inline;
    zoom: 1;
    border-left: none;
    border-right: none;
    position: relative;
}
  
  .topic .content h2, .topic-content h2, .topic .content h3, .topic-content h3, .topic .content h4, .topic-content h4, .topic .content h5, .topic-content h5, .topic .content h6, .topic-content h6 {
    color: #6DA3BD;
    font-size: 18px;
    margin-bottom: 5px;
    font-weight: normal;
    line-height: 1.2;
}
  .topic .content blockquote, .topic-content blockquote {
    font-style: italic;
    padding: 5px 10px;
    background: #282b2f;
    border-radius: 4px;

}
    
.coupon-vote-degree .vote-button {
    cursor: pointer;
    float: left;
    width: 32px;
    height: 32px;
    background:#555;
    border: 1px solid #666;
    border-radius: 18px;
    margin-right: 5px;
}  
    
.topic-coupon .coupon-info-text span {
    color: #ff8700;
    font-weight: bold;
}
}
