style.css:
@-moz-document domain("youtube.com") {
/* ==========================================================================
   VARIABLES
   ========================================================================== */

:root {
    --color-green: rgb(17, 146, 26);
    --color-red: rgb(204, 24, 30);
}

/* ==========================================================================
   "SUBSCRIBE" ELEMENT
   ========================================================================== */

/* Basic overrides
   ========================================================================== */

.yt-uix-button-subscribed-branded:before,
.yt-uix-subscription-preferences-button:before
{
    opacity: 1 !important;
}

.yt-uix-subscription-button
{
    color: white !important;
}

.yt-uix-subscription-preferences-button
{
    margin-left: 0 !important;
}

.yt-uix-subscription-button,
.yt-uix-subscription-preferences-button,
.yt-subscription-button-subscriber-count-branded-horizontal
{
    border-radius: 0 !important;
}

/* Bell button
   ========================================================================== */

/* Notifications: All */

.yt-uix-button-subscribed-branded,
.yt-uix-subscription-notifications-all
{
    background-color: var(--color-green) !important;
}

/* Notifications: Highlights only */

.yt-uix-subscription-notifications-highlights
{
    background-color: var(--color-red) !important;
}
}
