style.css:
@-moz-document regexp("(chrome|moz)-extension://.*") {

/*[[CompactStyle]]*/


html {
    background: hsl(0, 0%, 14%)!important;
    text-rendering: optimizeLegibility!important;
    -webkit-font-smoothing: antialiased!important;
}

.fixed-header #header {
